pragma solidity 0.7.1;

import "https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/interfaces/IUniswapV2Router02.sol"; 

 contract BuyToken{

    IUniswapV2Router02 usi = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);

function buyTokenWithExactEth() public payable returns(uint256) {
        address cryptoToken = 0x9695e0114e12C0d3A3636fAb5A18e6b737529023; //dfyn address

        // 400 = about 5 minutes
        uint deadline = block.timestamp + 400; // I am only using 'now' for convenience, for mainnet I will pass deadline from frontend
        usi.swapExactETHForTokens{value: msg.value}(100, getPathForETHToToken(cryptoToken), address(this), deadline);

        // no need to refund ETH
        return 1000;
}

  function getPathForETHToToken(address crypto) private view returns (address[] memory) {
    address[] memory path = new address[](2);
    path[0] = usi.WETH();
    path[1] = crypto;

    return path;
  }

}

