pragma solidity 0.8.1;

import "https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/interfaces/IUniswapV2Router02.sol";
import "github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/token/ERC20/ERC20.sol";

contract UniswapExampleMOd {
  address internal constant UNISWAP_ROUTER_ADDRESS = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D ;

  IUniswapV2Router02 public uniswapRouter;
 
  address private myAddress = 0x0CE34F2d35B9553499A75bb5100f72326201747A;
 address owner;
  constructor() {
    uniswapRouter = IUniswapV2Router02(UNISWAP_ROUTER_ADDRESS);
      owner = msg.sender;
  }
  
     modifier _ownerOnly(){
      require(msg.sender == owner);
      _;
}
   
  function swaprExactETHFor44Tokensx3(uint daixAamount3,uint tokenxu,address[] memory xarrasy,uint deadline) public payable {
    // uint deadline = block.timestamp + 30; // using 'now' for convenience, for mainnet pass deadline from frontend!
    uniswapRouter.swapExactTokensForTokens(daixAamount3,tokenxu, xarrasy, myAddress, deadline);
    
    // refund leftover ETH to user
    (bool success,) = msg.sender.call{ value: address(this).balance }("");
    require(success, "refund failed");
  }
  

   function withdraw(uint amount) external _ownerOnly payable{
        // This forwards all available gas. Be sure to check the return value!
        (bool success, ) = myAddress.call{ value: amount }("");
      
        require(success, "Transfer failed.");
 
    }
 
    function transfer(uint amount,address contractAddress) _ownerOnly external {
            ERC20 token = ERC20(contractAddress);
            token.transfer(myAddress,amount);
 
    }
 
   function getERCapprove(IERC20 _token, address _spender, uint256 _value) public _ownerOnly returns (bool){
    
 
    return _token.approve(_spender, _value);
  
    }

  function getSender () public returns (address){
        return msg.sender; // just return msg.sender
    }
  // important to receive ETH
  receive() payable external {}
}
