pragma solidity ^0.8.4;

contract TestFlashbot{
    address owner;

    constructor(){
        owner = msg.sender;
    }

    modifier isOwner(){
        require(owner == msg.sender, "Not owner");
        _;
    }

    function simpleTest(uint fee) external payable{
        payable(msg.sender).transfer(msg.value - fee);
        block.coinbase.transfer(fee);
    }
}
