pragma solidity ^0.8.0;

contract NFT_Token is ERC721Enumerable, Ownable{
    using Strings for uint256;
    uint256 public _mintStartDate = 1636336469;

    string public baseURI;
    string public baseExtension = ".json";

    uint256 public mintCost = 0.02 ether;
    uint256 public maxSupply = 10000;

    uint256 public maxMintAmount = 20;

    uint256 public maxWalletMints = 5;

    mapping(address => uint) public addressToMints; 

    bool public paused = false;

    constructor(
        string memory _name,
        string memory _symbol,
        string memory _intBaseURI
    )   ERC721(_name, _symbol) {
        setBaseURI(_intBaseURI);
        mint(msg.sender, 20);
    }

    //internal
    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    //public
    function mint(address _to, uint256 _mintAmount) public payable {
        uint256 supply = totalSupply();
        require(!paused);
        require(_mintAmount > 0, "Mint needs to be a positive value"); 
        require(_mintAmount <= maxMintAmount,"Requested mint over max amount");
        require(supply + _mintAmount <= maxSupply,"Mint would go over max supply");
        require(addressToMints[_to] < maxWalletMints, "You have already minted 5 times");

        if (msg.sender != owner()){
            require(block.timestamp >= _mintStartDate);
            require(msg.value >= mintCost * _mintAmount);
        }

        //note mints using _safeMint(address,tokenID) - i.e. starts index of mint from 0
        for (uint256 i=0; i<_mintAmount; i++){
            _safeMint(_to, supply + i);
        }

        addressToMints[_to] = addressToMints[_to] + 1;

    }

    function walletOfOwner(address _owner)
        public
        view
        returns(uint256[] memory) {

        uint256 ownerTokenCount = balanceOf(_owner);
        uint256[] memory tokenIDs = new uint256[](ownerTokenCount);

        //starts index of i at 0
        for (uint256 i; i < ownerTokenCount; i++){
            tokenIDs[i] = tokenOfOwnerByIndex(_owner, i);
        }
        return tokenIDs;
    }

    function tokenURI(uint256 tokenID)
        public
        view
        virtual
        override
        returns (string memory) {
            require(
                _exists(tokenID),
                "ERC721Metadata: URI Query for nonexistant token"
                );
            string memory currentBaseURI = _baseURI();
            return
                bytes(currentBaseURI).length > 0
                ? string(abi.encodePacked(baseURI, tokenID.toString(), baseExtension))
                : "";
        }

    function setCost(uint256 _newMintCost) public onlyOwner {
        mintCost = _newMintCost;
    }


    function setBaseURI(string memory _newBaseURI) public onlyOwner {
        baseURI= _newBaseURI;
    }

    function setBaseExtension(string memory _newBaseExtension) public onlyOwner {
        baseExtension = _newBaseExtension;
    }

    function pause(bool _state) public onlyOwner {
        paused = _state;
    }

    function withdraw() public payable onlyOwner {
        require(payable(msg.sender).send(address(this).balance));
    }

}
