pragma solidity ^0.6.6;

import './UniswapV2Library.sol';
import './interfaces/IUniswapV2Router02.sol';
import './interfaces/IUniswapV2Pair.sol';
import './interfaces/IUniswapV2Factory.sol';
import './interfaces/IERC20.sol';

contract Arbitrage {
  address public factory; //the factory of uniswap - a central hub in the Uniswap ecosystem
  uint constant deadline = 10 days; //the deadline for the max time the trade can exist
  IUniswapV2Router02 public sushiRouter; //pointer to the sushi router ,a central smart contract in the sushiswap - 
  //to execute trades in the sushi pools - sushi reuses uniswap code so that's why it's the same router interface

  constructor(address _factory, address _sushiRouter) public {
    factory = _factory;  
    sushiRouter = IUniswapV2Router02(_sushiRouter);
  }

  //function the trader calls to execute arbitrage - 
  //maybe a custom script that spots the price difference
  function startArbitrage(
    address token0, //addresses of two tokens for the arb e.g. ETH and DAI
    address token1, 
    uint amount0, //the token whose amount is not 0 is the token that we'll borrow from 
    uint amount1 //e.g. amount0 = 0, and amount1 = 1M and we'll borrow 1M of token1

  ) external {
    //get the address of the pair - the order doesn't matter
    //the pair smart contract is the liquidity pool of uniswap
    address pairAddress = IUniswapV2Factory(factory).getPair(token0, token1);
    require(pairAddress != address(0), 'This pool does not exist');

    //this is a lowlevel function to initiate the flashloan
    //normally you create another f'n but we need to directly call the direct contract
    //the amount that's not zero is the amount of tokens you want to borrow
    IUniswapV2Pair(pairAddress).swap(
      amount0, 
      amount1, 
      address(this), 
      bytes('not empty') //this indicates (triggers) it's a flash loan
    );
  }

//uniswap expects your smart contract to have this function
  function uniswapV2Call(
    address _sender, // the addr that triggered the flashloan - address of our arbitrage smart contract
    uint _amount0,  //the amount we want to borrow
    uint _amount1, //the amount we want to borrow
    bytes calldata _data //the data that we pass
  ) external {
    address[] memory path = new address[](2);
    uint amountToken = _amount0 == 0 ? _amount1 : _amount0;

    //get addresses of twos in Uniswaps liqudity pool
    address token0 = IUniswapV2Pair(msg.sender).token0(); 
    address token1 = IUniswapV2Pair(msg.sender).token1();

    //make sure the call comes the uniswap smart contract - we don't wnat nything to mess with arb
    require(
      msg.sender == UniswapV2Library.pairFor(factory, token0, token1), 
      'Unauthorized'
    ); 

    //make sure one amount is zero
    require(_amount0 == 0 || _amount1 == 0);

    //specify the direaction of the trade
    //if amount0 = 0 then on sushiswap we're selling token 1 for token
    //b/c remember we borrowed from token 1
    path[0] = _amount0 == 0 ? token1 : token0; 
    path[1] = _amount0 == 0 ? token0 : token1;

    //pointer to the token we're selling on sushiswap
    IERC20 token = IERC20(_amount0 == 0 ? token1 : token0);

    //allow the sushiswap router to sell our token
    token.approve(address(sushiRouter), amountToken);

    //calc the amount of token we will need to reimburse to the flashloan of uniswap
    uint amountRequired = UniswapV2Library.getAmountsIn(
      factory, 
      amountToken, 
      path
    )[0]; 

    //sell token borrowed from uniswap on sushiswap
    uint amountReceived = sushiRouter.swapExactTokensForTokens(
      amountToken, 
      amountRequired, 
      path, 
      msg.sender, 
      deadline 
    )[1]; //sell token borrow on sushiswap

    //pointer to the other token that we got as output from sushiswap
    IERC20 otherToken = IERC20(_amount0 == 0 ? token0 : token1);

    //a portion of token is used to reiumburse flash loan
    otherToken.transfer(msg.sender, amountRequired);

    //this is the profit 
    //tx.origin is the address that initiated teh whole transaction (myself or script used to monitor the price difference)
    otherToken.transfer(tx.origin, amountReceived - amountRequired); 
  }
}

