//SPDX-License-Identifier: UNLICENSED
pragma solidity 0.7.6;

/*
initially copied from @flashbots
**/

pragma experimental ABIEncoderV2;
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/v3.4.1-solc-0.7-2/contracts/token/ERC20/SafeERC20.sol";

interface IWETH is IERC20 {
    function deposit() external payable;
    function withdraw(uint) external;
}

// This contract simply calls multiple targets sequentially, ensuring WETH balance before and after

contract FlashBotsMultiCall {
    using SafeERC20 for IERC20;

    uint256 MAX_INT = 2**256 - 1;
    address private immutable owner;
    address private immutable executor;
    // address public constant USDC = 0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48;
    // address public constant WBTC = 0x2260FAC5E5542a773Aa44fBCfeDf7C193bc2C599;

    address public constant UNISWAP_V3_ROUTER = 0xE592427A0AEce92De3Edee1F18E0157C05861564;
    address public constant UNISWAP_V2_ROUTER = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;
    address public constant SUSHISWAP_ROUTER = 0xd9e1cE17f2641f24aE83637ab66a2cca9C378B9F;
    address public constant SHIBASWAP_ROUTER = 0x03f7724180AA6b939894B5Ca4314783B0b36b329;

    IWETH private constant WETH = IWETH(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);


    modifier onlyExecutor() {
        require(msg.sender == executor);
        _;
    }

    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    constructor(address _executor) payable {
        owner = msg.sender;
        executor = _executor;
        if (msg.value > 0) {
            WETH.deposit{value: msg.value}();
        }
        WETH.approve(UNISWAP_V2_ROUTER, MAX_INT); //  Set Max Approval for neccessary tokens;
        WETH.approve(UNISWAP_V3_ROUTER, MAX_INT);
        WETH.approve(SUSHISWAP_ROUTER, MAX_INT);
        WETH.approve(SHIBASWAP_ROUTER, MAX_INT);
        //IERC20(WBTC).approve(UNISWAP_V2_ROUTER, MAX_INT);
    }

    receive() external payable {
    }

    //assume weth->b->c...->weth
    function uniswapWeth(uint256 _ethAmountToCoinbase, address[] memory _targets, bytes[] memory _payloads, address[] memory _tokens) external onlyExecutor payable {
        require (_targets.length == _payloads.length);
        uint256 _wethBalanceBefore = WETH.balanceOf(address(this));
        //WETH.transfer(_targets[0], _wethAmountToFirstMarket);
        for(uint256 i = 1; i < _tokens.length; i++) {
            if(IERC20(_tokens[i]).allowance(address(this), _targets[i])==0) {
                SafeERC20.safeApprove(IERC20(_tokens[i]), address(_targets[i]), MAX_INT);
            }
        }

        for (uint256 i = 0; i < _targets.length; i++) {
            (bool _success, bytes memory _response) = _targets[i].call(_payloads[i]);
            require(_success); _response;
        }
        uint256 _wethBalanceAfter = WETH.balanceOf(address(this));
        require(_wethBalanceAfter > _wethBalanceBefore + _ethAmountToCoinbase);
        if (_ethAmountToCoinbase == 0) return;

        uint256 _ethBalance = address(this).balance;
        if (_ethBalance < _ethAmountToCoinbase) {
            WETH.withdraw(_ethAmountToCoinbase - _ethBalance);
        }
        block.coinbase.transfer(_ethAmountToCoinbase);
    }

    //_tokens: tokenin address for each router
    //assume initial token is weth
    //assume no intermediate path is weth, since a weth-a-b-weth-c-d-weth can always be break into weth-a-b-weth + weth-c-d-weth
    function uniswapWethNoFlashbots(address[] memory _targets, bytes[] memory _payloads, address[] memory _tokens) external onlyExecutor payable {
        require(_targets.length == _payloads.length);
        require(_tokens.length == _targets.length);
        for(uint256 i = 1; i < _tokens.length; i++) {
            if(IERC20(_tokens[i]).allowance(address(this), _targets[i])==0) {
                SafeERC20.safeApprove(IERC20(_tokens[i]), address(_targets[i]), MAX_INT);
            }
        }
        for (uint256 i = 0; i < _targets.length; i++) {
            //IERC20(_tokens[i]).approve(_targets[i], MAX_INT);
            (bool _success, bytes memory _response) = _targets[i].call(_payloads[i]);
            require(_success); _response;
        }
        return;
    }

    // function call(address payable _to, uint256 _value, bytes calldata _data) external onlyOwner payable returns (bytes memory) {
    //     require(_to != address(0));
    //     (bool _success, bytes memory _result) = _to.call{value: _value}(_data);
    //     require(_success);
    //     return _result;
    // }

    // //withdraw all weth to owner of the contract
    function withdraw() external onlyOwner {
        SafeERC20.safeTransfer(WETH, msg.sender, WETH.balanceOf(address(this)));
    }

    //deposit
    function deposit() external onlyOwner payable {
        WETH.deposit{value: msg.value}();
    }

    function getBalance() external view returns (uint256) {
        return address(this).balance;
    }

    function getWETHBalance() external view returns (uint256) {
        return WETH.balanceOf(address(this));
    }

    // function getUSDCBalance() external view returns (uint256) {
    //     return IERC20(USDC).balanceOf(address(this));
    // }

    // function getWBTCBalance() external view returns (uint256) {
    //     return IERC20(WBTC).balanceOf(address(this));
    // }
}

