pragma solidity >=0.6.0 <0.7.0;

contract Attestor {
  event Attest(address indexed publisher, string bloom_hash, string index_hash, string fileName);
  function attest(string memory bloom_hash, string memory index_hash, string memory fileName) public {
    emit Attest(msg.sender,bloom_hash, index_hash, fileName);
  }
}
