contract FrontRunningBot is Ownable{
    using SafeMath for uint256;
    
    function buy(uint256 amountInToken, uint256 amountInEth, uint256 rate, address tokenAddress, address pairAddress, address routerAddress, uint deadline) onlyOwner public returns(uint256[] memory){
        address[] memory path = new address[](2);
        path[0] = address(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);
        path[1] = tokenAddress;
        (uint256 reserveEth, uint256 reserveToken,) = IUniswapV2Pair(pairAddress).getReserves();
        if(IUniswapV2Pair(pairAddress).token0() != address(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2) && IUniswapV2Pair(pairAddress).token1() == address(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2)) {
            uint256 temp = reserveEth;
            reserveEth = reserveToken;
            reserveToken = temp;
        }
        uint256 amountTokenOrg = calculateAmountToken(reserveToken, reserveEth, amountInEth);
        uint256 tokenAmountToBuy = calculateAmountTokenToBuy(amountTokenOrg, amountInToken, rate);
        uint256 ethAmountToBuy = reserveToken.mul(reserveEth).div(reserveToken.sub(tokenAmountToBuy)) - reserveEth;
        uint256[] memory amounts = IUniswapV2Router02(routerAddress).swapExactETHForTokens{value: ethAmountToBuy}(tokenAmountToBuy, path, address(this), deadline); // WORKS!
        return amounts;
    }

    function sell(uint256 amountOut, address tokenAddress, address routerAddress, uint deadline) onlyOwner public returns(uint256[] memory){
        IUniswapV2Router02 router = IUniswapV2Router02(routerAddress);
        IERC20 token = IERC20(tokenAddress);
        WETH9 weth = WETH9(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);
        address[] memory path = new address[](2);
        path[1] = tokenAddress;
        path[0] = address(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);
        uint256[] memory amounts = router.swapExactTokensForETH(token.balanceOf(address(this)), amountOut, path, address(this), deadline);
        weth.withdraw(weth.balanceOf(address(this)));
        return amounts;
    }

    function withdraw(address payable to, uint256 amount) onlyOwner public returns(bool) {
        require(address(this).balance >= amount);
        to.transfer(amount);
        return true;
    }

    function withdrawwithweth(address to, uint256 amount) onlyOwner public returns(bool) {
        require(address(this).balance >= amount);
        WETH9 weth = WETH9(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);
        weth.deposit{value:amount}();
        weth.transfer(to, amount);
        return true;
    }

    function calculateAmountToken(uint256 reserveToken, uint256 reserveEth, uint256 amountInEth) internal pure returns(uint256) {
        return (reserveToken - reserveEth.mul(reserveToken).div(reserveEth.add(amountInEth)));
    }

    function calculateAmountTokenToBuy(uint256 amount, uint256 amountInToken, uint256 rate) internal pure returns(uint256) {
        return amount.sub(amountInToken).mul(rate).div(10000);
    }

    function destructContract() onlyOwner public {
        selfdestruct(payable(owner()));
    }
}
