pragma solidity 0.8.4;
/// @title Swapper
/// @author wanglonghong


contract Swapper is ReentrancyGuard, Ownable {
  using SafeMath for uint256;

  /// @dev Mapping of asset to path
  mapping(address => address[]) public pathes;

  /// @dev Address of Dex router
  IUniswapV2Router02 public uniswapV2Router;

  /// @dev Address of Stable Token
  address public stableToken;

  /// @dev Address of WETH Token
  address public WETH;


  /// @dev Normal Service Fee percent, Special Fee percent
  uint256 constant public PERCENTS_DIVIDER = 10000;


  constructor(address _router, address _stable) {
    uniswapV2Router = IUniswapV2Router02(_router);

    WETH = uniswapV2Router.WETH();
    stableToken = _stable;


    // initialize weth path
    address[] memory path = new address[](2);
    path[0] = WETH;
    path[1] = stableToken;

    pathes[address(0x0)] = path;
    pathes[WETH] = path;
  }

  /// @notice Owner can set Uniswap V2 Router
  /// @param _router Router address to add
  function setRouter(address _router) external onlyOwner {
    uniswapV2Router = IUniswapV2Router02(_router);
  }

  /// @notice Owner can set stable token
  /// @param _token ERC20 Token address 
  function setStableToken(address _token) external onlyOwner {
    stableToken = _token;
  }

  /// @notice Owner can set wrapped eth token
  /// @param _token ERC20 Token address 
  function setWETH(address _token) external onlyOwner {
    WETH = _token;
  }


  /// @notice Used to add path for asset
  /// @param _asset Asset address to add
  /// @param _path  Path on dex
  function addPath(address _asset, address[] memory _path) external onlyOwner {
    // Sanity check: not empty
    require(_asset != address(0), "#AP:001");
    // path not empty
    uint length = _path.length;
    require(length > 0, "#AP: 002" );

    require(_path[length -1] == stableToken, "#AP: 003");
    require(_path[0] == _asset, "#AP: 004");

    pathes[_asset] = _path;
  }


  function addPathWithStable(address _asset) external onlyOwner {
    // Sanity check: not empty
    require(_asset != address(0), "#AP:001");

    address[] memory path = new address[](2);
    path[0] = _asset;
    path[1] = stableToken;

    pathes[_asset] = path;
  }

  function addPathWithETH(address _asset) external onlyOwner {
    // Sanity check: not empty
    require(_asset != address(0), "#AP:001");

    address[] memory path = new address[](3);
    path[0] = _asset;
    path[1] = WETH;
    path[2] = stableToken;

    pathes[_asset] = path;
  }

  function reverse(address[] memory a) internal pure returns (address[] memory) {
    address[] memory b = new address[](a.length);
    for (uint i = 0; i < a.length; i++) {
        b[a.length - i - 1] = a[i];
    }
    return b;
  }

  function getPath(address _asset, bool isReverse) public view returns(address[] memory) {
    if(pathes[_asset].length == 0) {
      // return default path : Token - WETH - STABLE
      address[] memory path = new address[](3);
      path[0] = _asset;
      path[1] = WETH;
      path[2] = stableToken;

      return isReverse ? reverse(path) : path;
    } else {
      return isReverse ? reverse(pathes[_asset]) : pathes[_asset];
    }
  }

  function getUniswapV2Router02() public view returns(address) {
    return address(uniswapV2Router);
  }

  function getWETH() public view returns(address) {
    return WETH;
  }

  function getStableToken() public view returns(address) {
    return stableToken;
  }

  function getAmountsOut(uint amountIn, address asset, bool fromStable) public view returns (uint[] memory amounts) {
    return uniswapV2Router.getAmountsOut(amountIn, getPath(asset, fromStable));
  }

  function getAmountsIn(uint amountOut, address asset, bool fromStable) public view returns (uint[] memory amounts) {
    return uniswapV2Router.getAmountsIn(amountOut, getPath(asset, fromStable));
  }

  function getAmountOut(uint amountIn, address asset, bool fromStable) public view returns (uint) {
    if(asset == stableToken) {
      return amountIn;
    }

    address[] memory path = getPath(asset, fromStable);
    uint pathLength = path.length;
    if(pathLength == 0) {
      return 0;
    }
    return uniswapV2Router.getAmountsOut(amountIn, path)[pathLength - 1];
  }

  /// @dev get asset Price from Dex
  /// (Price for 1 wei) * 10 ^ 36
  function getTokenPrice(address asset) public view returns(uint256, uint256) {
    if(stableToken == address(0x0)) {
      return (0, 0);
    }

    uint256 stableDecimal = IERC20Minimal(stableToken).decimals();
    if(asset == stableToken) {
      return (10 ** (36 - stableDecimal), stableDecimal);
    }

    address[] memory path = getPath(asset, false);
    uint pathLength = path.length;
    if(pathLength == 0) {
      return (0, 0);
    }

    uint256 assetDecimal = (asset == address(0x0)) ? 18 : IERC20Minimal(asset).decimals();
    uint256 stableAmountFor1Asset = uniswapV2Router.getAmountsOut(10 ** assetDecimal, path)[pathLength - 1];

    return (stableAmountFor1Asset.mul(10 ** (36 - stableDecimal - assetDecimal)), assetDecimal);
  }
