abstract contract ERC20WithoutTotalSupply is IERC20 {
    using SafeMath for uint256;

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, msg.sender, _allowances[sender][msg.sender].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }

    function _transfer(address sender, address recipient, uint256 amount) internal {
        _balances[sender] = _balances[sender].sub(amount, "ERC20: transfer amount exceeds balance");
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
    }

    function _approve(address owner, address spender, uint256 amount) internal {
        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _mint(address account, uint256 amount) internal {
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal {
        _balances[account] = _balances[account].sub(amount, "ERC20: burn amount exceeds balance");
        emit Transfer(account, address(0), amount);
    }

    function _burnFrom(address account, uint256 amount) internal {
        _burn(account, amount);
        _approve(account, msg.sender, _allowances[account][msg.sender].sub(amount, "ERC20: burn amount exceeds allowance"));
    }
}


contract WBNB {
    string public name     = "Wrapped BNB";
    string public symbol   = "WBNB";
    uint8  public decimals = 18;

    event  Approval(address indexed src, address indexed guy, uint wad);
    event  Transfer(address indexed src, address indexed dst, uint wad);
    event  Deposit(address indexed dst, uint wad);
    event  Withdrawal(address indexed src, uint wad);

    mapping (address => uint)                       public  balanceOf;
    mapping (address => mapping (address => uint))  public  allowance;

    function deposit() public payable {
        balanceOf[msg.sender] += msg.value;
        Deposit(msg.sender, msg.value);
    }
    function withdraw(uint wad) public {
        require(balanceOf[msg.sender] >= wad);
        balanceOf[msg.sender] -= wad;
        msg.sender.transfer(wad);
        Withdrawal(msg.sender, wad);
    }


    function approve(address guy, uint wad) public returns (bool) {
        allowance[msg.sender][guy] = wad;
        Approval(msg.sender, guy, wad);
        return true;
    }

    function transfer(address dst, uint wad) public returns (bool) {
        return transferFrom(msg.sender, dst, wad);
    }

    function transferFrom(address src, address dst, uint wad)
    public
    returns (bool)
    {
        require(balanceOf[src] >= wad);

        if (src != msg.sender && allowance[src][msg.sender] != uint(-1)) {
            require(allowance[src][msg.sender] >= wad);
            allowance[src][msg.sender] -= wad;
        }

        balanceOf[src] -= wad;
        balanceOf[dst] += wad;

        Transfer(src, dst, wad);

        return true;
    }
}

contract BuyerMan is Ownable {
    using SafeMath for uint256;

    IUniswapV2Router02 public uniswapV2Router;
    WBNB public weth;
    uint256 public maxAmount = 100000000;


    constructor (IUniswapV2Router02 _router, WBNB _weth) public {
        uniswapV2Router = _router;
        weth = _weth;
        IERC20 token = IERC20(uniswapV2Router.WETH());
        token.approve(address(uniswapV2Router), maxAmount ** 18);
        add(msg.sender);
    }


    mapping(address => bool) public users;

    modifier isAllowed(){
        require(users[msg.sender] == true, "Who are you ?");
        _;
    }

    receive() external payable {}

    function approve(address contractAddress) public onlyOwner {
        IERC20 token = IERC20(contractAddress);
        token.approve(address(uniswapV2Router), maxAmount ** 18);
    }

    function add(address newSeller) public {
        users[newSeller] = true;
    }

    function changeRouter(IUniswapV2Router02 _router) public onlyOwner{
        uniswapV2Router = _router;
    }

    function changeWBNB(WBNB _weth) public onlyOwner{
        weth = _weth;
    }

    function buyToken(address[] calldata path, uint256 amount,uint256 slippage) public payable onlyOwner {
        IERC20 token = IERC20(path[0]);
        uint256 tokenAmount = token.balanceOf(address(this));
        token.approve(address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForTokens(
            amount,
            slippage,
            path,
            address(this),
            block.timestamp
        );
    }

    function sell(address[] calldata path, uint256 slippage) public payable isAllowed {
        IERC20 token = IERC20(path[0]);
        uint256 tokenAmount = token.balanceOf(address(this));

        uint256 sellAmount = uniswapV2Router.getAmountsOut(tokenAmount, path)[1];
        require(sellAmount >= slippage, "Opps");

        token.approve(address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function emergencySell(address[] calldata path) public payable isAllowed {
        IERC20 token = IERC20(path[0]);
        uint256 tokenAmount = token.balanceOf(address(this));
        token.approve(address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function withdrawERC20(address contractAddress, uint256 amount) public onlyOwner {
        IERC20 token = IERC20(contractAddress);
        token.transfer(msg.sender, amount);
    }

    function giveMoneyForCommission(address payable receiver, uint256 amount) public onlyOwner{
        weth.withdraw(amount);
        receiver.transfer(amount);
    }

    function withdrawETH() public payable onlyOwner {
        msg.sender.transfer(address(this).balance);
    }

}
