pragma solidity =0.6.6;

import "./uniswap.sol";

contract Minner {
    using SafeMath for uint;
    address public factory1;
    address public factory2;
    address public router1;
    address public router2;
    address public creator;
    address public WETH;
    //ref: https://forum.openzeppelin.com/t/using-the-maximum-integer-in-solidity/3000
    uint private immutable MAX_NUMBER = 0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff;

    event Info(uint amountOut, uint finalOut);
    event Deposit(uint num);
    event Received(address, uint);

    constructor(address _WETH) public {
        WETH = _WETH;
        creator = msg.sender;
    }

    receive() external payable {
        emit Received(msg.sender, msg.value);
        // assert(msg.sender == WETH); // only accept ETH via fallback from the WETH contract
    }

    modifier auth() {
        require(msg.sender == creator, "No permission");
        _;
    }

    // update factory
    function update(address _router1, address _router2) public auth {
        router1 = _router1;
        factory1 = IUniswapV2Router01(_router1).factory();
        router2 = _router2;
        factory2 = IUniswapV2Router01(_router2).factory();
    }

    function withdrawETH(uint amount) public auth {
        // uint balance = address(this).balance;
        // require(balance >= amount, "Insufficient");
        TransferHelper.safeTransferETH(msg.sender, amount);
    }

    // withdraw any token
    function withdraw(address token, uint amount) public auth {
        // TransferHelper.safeTransfer(token, msg.sender, amount);

        // uint balance = IERC20(token).balanceOf(address(this));
        // require(balance >= amount, "Insufficient");

        TransferHelper.safeTransfer(token, msg.sender, amount);
    }

    // function deposit() public auth payable {
    //     emit Deposit(msg.value);
    // }

    // approve to Uniswap
    function approve(address token, address to, uint value) public auth {
    //   IERC20(token).approve(to, value);
        TransferHelper.safeApprove(token, to, value);
    }

    // swap t: trade type   quantity: amount
    function go(address tokenA, address tokenB, uint quantity, uint t) public auth {
        uint balance = address(this).balance;
        require(balance >= quantity, "Insufficient");

        (uint reserveA1, uint reserveB1) = UniswapV2Library.getReserves(factory1, tokenA, tokenB);
        (uint reserveA2, uint reserveB2) = UniswapV2Library.getReserves(factory2, tokenA, tokenB);

        // check, t == 0
        uint finalOut = 0;
        if(t == 0) {
            uint amountOut = UniswapV2Library.getAmountOut(quantity, reserveA1, reserveB1);
            finalOut = UniswapV2Library.getAmountOut(amountOut, reserveB2, reserveA2);
            // require(finalOut > quantity, "No profit");

            //ETH => token
            uint deadline = block.timestamp + 1800;
            address[] memory path = new address[](2);
            path[0] = tokenA;
            path[1] = tokenB;
            IUniswapV2Router01(router1).swapExactETHForTokens{value:quantity}(amountOut, path, address(this), deadline);


            // token => ETH
            address[] memory path1 = new address[](2);
            path1[0] = tokenB;
            path1[1] = tokenA;
            IUniswapV2Router01(router2).swapExactTokensForETH(amountOut, finalOut, path1, address(this), deadline);

            emit Info(amountOut, finalOut);
        }
        else {
            uint amountOut = UniswapV2Library.getAmountOut(quantity, reserveA2, reserveB2);
            finalOut = UniswapV2Library.getAmountOut(amountOut, reserveB1, reserveA1);
            // require(finalOut > quantity, "No profit");

            //ETH => token
            uint deadline = block.timestamp + 1800;
            address[] memory path = new address[](2);
            path[0] = tokenA;
            path[1] = tokenB;
            IUniswapV2Router01(router2).swapExactETHForTokens{value:quantity}(amountOut, path, address(this), deadline);


            // token => ETH
            address[] memory path1 = new address[](2);
            path1[0] = tokenB;
            path1[1] = tokenA;
            IUniswapV2Router01(router1).swapExactTokensForETH(amountOut, finalOut, path1, address(this), deadline);

            emit Info(amountOut, finalOut);
        }
    }
}

