pragma solidity >= 0.5.0 < 0.7.0;

import "./SafeMath.sol";

contract MyBank {

  using SafeMath for uint256;

  uint256 minDeposit = 200000000000000;
    uint256 public initialBlockNumber;
    mapping(address => uint256) public deposits;
    address owner;

    constructor() public{
        owner = msg.sender;
        initialBlockNumber = block.number;
    }

    function deposit() public payable{
        uint256 currentBlock = block.number;   
        assert(currentBlock > initialBlockNumber); 
        uint256 blockDifference = currentBlock.sub(initialBlockNumber);
        require(msg.value >= blockDifference.mul(minDeposit), "Non minimum value achieved");
        deposits[msg.sender] = deposits[msg.sender].add(msg.value);
    }

    function getNextMininumDeposit() public view returns (uint256){
        uint256 nextBlock = block.number.add(1);
        uint256 blockDifference = nextBlock.sub(initialBlockNumber);
        return blockDifference.mul(minDeposit);
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "You need to be the owner of the contract to execute this function");
        _;
    }

    function withdraw() public onlyOwner{
        uint256 currentBalance = address(this).balance; 
        address payable currentOwner = msg.sender;
        currentOwner.transfer(currentBalance);
    }
}

