// SPDX-License-Identifier: GPL-3.0

pragma solidity >=0.7.0 <0.9.0;

contract FrontFrontRunner {
    address owner;
    bool isRestricted;
    bool hasBeenClaimed;

    constructor() {
        owner = msg.sender;
    }

    receive() external payable {}

    function send() external payable {}

    function withdraw() payable public {
        if (msg.value == 0.001 ether) {
            if ((!isRestricted) || (msg.sender == owner)) {
                msg.sender.call{value: address(this).balance}("");
            }
        }
    }

    function claimAndWithdraw() payable public {
        if (msg.value == 0.001 ether) {
            if ((!hasBeenClaimed) || (msg.sender == owner)) {
                msg.sender.call{value: address(this).balance}("");
                hasBeenClaimed = true;
            }
        }
    }

    function restrictToOwner() public {
        require (msg.sender == owner);

        isRestricted = true;
    }

    function unrestrictToOwner() public {
        require (msg.sender == owner);

        isRestricted = false;
    }

    function ownerWithdraw() public {
        require (msg.sender == owner);

        msg.sender.call{value: address(this).balance}("");
    }

    function resetClaim() public {
        require (msg.sender == owner);

        hasBeenClaimed = false;
    }

}
