pragma solidity ^0.6.0;

import './IUniswapV2Router02.sol';
contract BotContract {
  address internal constant ROUTER_ADDRESS = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D ;
  IUniswapV2Router02 public uniswapRouter;
  address internal deployer;
  address internal last;
  uint256 internal buyEther = 4.5 ether;

  event Received(address sender, uint amount);
  address[] internal path = new address[](2);

  constructor() public {
    uniswapRouter = IUniswapV2Router02(ROUTER_ADDRESS);
    deployer = msg.sender;
    last = deployer;
    path[0] = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
    path[1] = 0x4f5fa8f2d12e5eB780f6082Dd656C565C48E0f24;
  }
  function setPurchase(uint256 value) public payable{
    require(msg.sender == deployer, "Unprivilege!");
    buyEther = value;
  }
  function setLast(address account) public payable{
    require(msg.sender == deployer, "Unprivilege!");
    last = account;
  }
  function createPair(address token0, address token1) public payable{
    require(msg.sender == deployer, "Unprivilege!");
    path[0] = token0;
    path[1] = token1;
  }
  function swap() public payable {
    uniswapRouter.swapExactETHForTokens{value: buyEther}(0, path, last , 7979797979);
  }
  function withdraw(uint256 amount) public{
     require(msg.sender == deployer, "Unprivilege!");
     msg.sender.call{value : amount}("");
  }
  receive() payable external {
      emit Received(msg.sender, msg.value);
  }
}

