// SPDX-License-Identifier: MIT

pragma solidity >=0.4.22 <0.9.0;

import "./ICommission.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./IVoiceStreet.sol";
import "./IVoiceStreetNft.sol";

// 主网部署地址
//     0x1502b34052f0a0e025da62831eaf2677d69a897a
contract CommissionContract is ICommission, Ownable {
    struct CommissionRate {
        // 是否使用自定义的分成方式，默认是false，使用默认的分成
        bool custom;
        // NFT第一次转手后，每卖出一个产品平台方佣金
        uint16 firstSell;
        // NFT第一次卖出后，平台方的佣金
        uint16 platform;
        // NFT第一次转手后，每卖出一个产品创造者佣金
        uint16 creator;
        // 从购买者收取的费用
        uint16 buyer;
    }
    using SafeERC20 for IERC20;

    uint16 constant private PERCENT_DENOMINATOR = 10000;

    CommissionRate private _defaultRate;
    Ownable private _nftToken;
    IVoiceStreetNft private _vsContract;

    // 记录NFT的转售次数和销售分成方案
    mapping (uint256 => CommissionRate) private _tokenCommissionRate;
    mapping (uint256 => Dividen[]) private _tokenCreator;

    constructor(address nft) {
        _defaultRate = CommissionRate(false, 150, 75, 75, 25);
        _nftToken = Ownable(nft);
        _vsContract = IVoiceStreetNft(nft);
    }

    function getCreators(uint256 _tokenId) public override view returns (Dividen[] memory) {
        Dividen[] memory creators = _tokenCreator[_tokenId];
        if (creators.length == 0) {
            creators = new Dividen[](1);
            creators[0] = Dividen(_vsContract.tokenMeta(_tokenId).minter, PERCENT_DENOMINATOR);
        }

        return creators;
    }

    function setCreators(uint256 _tokenId, address[] memory _creators, uint16[] memory _dividens) public override {
        require(_creators.length == _dividens.length, "CommissionContract_setCreators_mismatch_creators_dividens");
        require(msg.sender == _nftToken.owner() || msg.sender == _vsContract.tokenMeta(_tokenId).minter, 
            "CommissionContract_setCreators_no_owner_or_minter");

        Dividen[] storage creators = _tokenCreator[_tokenId];
        uint256 sum = 0;
        for (uint8 i = 0; i < _creators.length; ++i) {
            sum = SafeMath.add(sum, _dividens[i]);
            creators.push(Dividen(_creators[i], _dividens[i]));
        }
        require(sum <= 10000, "CommissionContract_addCreator_more_than_100%_dividen");
    }

    function resetCreators(uint256 _tokenId) public override {
        require(msg.sender == _nftToken.owner() || msg.sender == _vsContract.tokenMeta(_tokenId).minter, 
            "CommissionContract_resetCreators_no_owner_or_minter");
        Dividen[] storage creators = _tokenCreator[_tokenId];
        for (uint8 i = 0; i < creators.length; ++i) {
            delete creators[i];
        }

        delete _tokenCreator[_tokenId];
    }

    function isCreator(uint256 _tokenId, address account) public view returns (bool) {
        Dividen[] memory creators = _tokenCreator[_tokenId];
        for (uint8 i = 0; i < creators.length; ++i) {
            if (creators[i].creator == account) return true;
        }

        return false;
    }

    function setDefaultRate(uint16 firstSell, uint16 platform, uint16 creator, uint16 buyer) public onlyOwner {
        _defaultRate.firstSell = firstSell;
        _defaultRate.platform = platform;
        _defaultRate.creator = creator;
        _defaultRate.buyer = buyer;
    }

    function getDefaultRate() public view returns(uint16 firstSell, uint16 platform, uint16 creator, uint16 buyer) {
        return (_defaultRate.firstSell, _defaultRate.platform, _defaultRate.creator, _defaultRate.buyer);
    }

    function setCommissionRate(uint256 _tokenId, uint16 firstSell, uint16 platformRate, uint16 creatorRate, uint16 buyerRate) public override onlyOwner {
        require(firstSell <= 1000);
        require(SafeMath.add(platformRate, creatorRate) <= 1000);

        // 平台方可以设置NFT被售出的次数
        _tokenCommissionRate[_tokenId].custom = true;
        _tokenCommissionRate[_tokenId].firstSell = firstSell;
        _tokenCommissionRate[_tokenId].platform = platformRate;
        _tokenCommissionRate[_tokenId].creator = creatorRate;
        _tokenCommissionRate[_tokenId].buyer = buyerRate;
    }

    function getCommissionRate(uint256 _tokenId) public override view onlyOwner returns (uint16 firstSell, uint16 platformRate, uint16 creatorRate, uint16 buyerRate) {
        return getCommissionRateImpl(_tokenId);
    }

    function getCommissionRateImpl(uint256 _tokenId) private view returns (uint16 firstSell, uint16 platformRate, uint16 creatorRate, uint16 buyerRate) {
        CommissionRate memory rate_ = _tokenCommissionRate[_tokenId];
        if (rate_.custom) {
            return (rate_.firstSell, rate_.platform, rate_.creator, rate_.buyer);
        } else {
            return getDefaultRate();
        }
    }

    // event DebugLog(string msg, uint256 p, uint256 c, uint256 s, address d);
    function calcCommission(uint256 _tokenId, uint256 _value, uint256 tokenSoldTimes) public view override returns (uint256 platform, uint256 creator, uint256 buyer) {
        (uint16 firstSell, uint16 platformRate, uint16 creatorRate, uint16 buyerRate) = getCommissionRateImpl(_tokenId);
        platform = 0;
        creator = 0;
        if (tokenSoldTimes == 0) {
            platform = SafeMath.mul(firstSell, _value);
        } else {
            platform = SafeMath.mul(platformRate, _value);
            // 创造者抽佣
            creator = SafeMath.mul(creatorRate, _value);
            creator = SafeMath.div(creator, 1000);
        }
        // 平台抽佣
        platform = SafeMath.div(platform, 1000);
        // 买方需支付的手续费
        buyer = SafeMath.mul(buyerRate, _value);
        buyer = SafeMath.div(buyer, 1000);
    }

    function divide(address _money, uint256 _tokenId, uint256 _amount) public override {
        Dividen[] memory creators = getCreators(_tokenId);
        for (uint8 i = 0; i < creators.length; ++i) {
            Dividen memory creator = creators[i];
            uint256 commission = SafeMath.mul(_amount, creator.dividen);
            commission = SafeMath.div(commission, PERCENT_DENOMINATOR);
            doTransfer(_money, creator.creator, commission);
        }
    }

    function doTransfer(address _money, address _beneficial, uint256 _amount) private {
        if (_money == address(0)) {
            // emit DebugLog("CommissionContract_divide1", _amount, 0, address(this).balance, _beneficial);
            payable(_beneficial).transfer(_amount);
        } else {
            // emit DebugLog("CommissionContract_divide2", _amount, 0, IERC20(_money).balanceOf(address(this)), _beneficial);
            IERC20(_money).safeTransfer(_beneficial, _amount);
        }
    }

    event Received(address, uint);
    receive() external payable {
        emit Received(msg.sender, msg.value);
    }
}

