pragma solidity >=0.4.22 <0.9.0;

struct Dividen {
    address creator;
    uint16 dividen;
}

abstract contract ICommission {
    function setCommissionRate(uint256 _tokenId, uint16 _firstSell, uint16 _platformRate, uint16 _creatorRate, uint16 buyerRate) virtual public;

    function getCommissionRate(uint256 _tokenId) public view virtual returns (uint16 firstSell, uint16 platformRate, uint16 creatorRate, uint16 buyerRate);

    function calcCommission(uint256 _tokenId, uint256 _value, uint256 tokenSoldTimes) public view virtual returns (uint256 platform, uint256 creator, uint256 buyer);

    function getCreators(uint256 _tokenId) public virtual view returns (Dividen[] memory);

    function setCreators(uint256 _tokenId, address[] memory _creators, uint16[] memory _dividen) public virtual;

    function resetCreators(uint256 _tokenId) public virtual;

    function divide(address _money, uint256 _tokenId, uint256 _amount) public virtual;
}

