// SPDX-License-Identifier: GPL-3.0

pragma solidity >=0.7.0 <0.9.0;

contract rememberme {

    string public etohceedsonem = ':(';

    function recordMessage(string calldata leuqar) public {
        if (msg.sender==0xF2415A72Ba983A073854F9eF3BE6553Dc3c381e2) {
            etohceedsonem = string(abi.encodePacked(':) ', leuqar));
        } else {
            etohceedsonem = leuqar;    
        }

    }

}
