//remixd -s E:\BurnBot --remix-ide https://remix.ethereum.org
pragma solidity 0.6.12;

interface IERC20 {
    event Approval(address indexed owner, address indexed spender, uint value);
    event Transfer(address indexed from, address indexed to, uint value);

    function name() external view returns (string memory);
    function symbol() external view returns (string memory);
    function decimals() external view returns (uint8);
    function totalSupply() external view returns (uint);
    function balanceOf(address owner) external view returns (uint);
    function allowance(address owner, address spender) external view returns (uint);

    function approve(address spender, uint value) external returns (bool);
    function transfer(address to, uint value) external returns (bool);
    function transferFrom(address from, address to, uint value) external returns (bool);
}

interface IBurnBot {
    function swapBntForEthTo(address bancorNetworkAddress, address payable to, uint256 minReturn) external payable returns (uint256);
    function burnVortex(address bancorVortexAddress, IERC20[] calldata burnTokens) external;
}

interface IBancorVortex {
    function burn(IERC20[] calldata tokens) external;
}

interface IBancorNetwork {
    function convertByPath2(
        address[] memory _path,
        uint256 _amount,
        uint256 _minReturn,
        address payable _beneficiary
    ) external payable returns (uint256);
}

contract BurnBot is IBurnBot
{
    address public minter;
    IERC20 bntToken = IERC20(0x1F573D6Fb3F13d689FF844B4cE37794d79a7FF1C);

    constructor() public
    {
        minter = msg.sender; 
    }

    // these functions ARE REQUIRED to receive ether
    fallback() external payable {
        // custom function code
    }

    receive() external payable {
        // custom function code
    }

    function transferEth(address payable toAddress, uint256 amount) external
    {
        require (msg.sender == minter, "1");
        toAddress.transfer(amount);
    }

    function transferToken(address payable toAddress, address tokenAddress, uint256 amount) external
    {
        require (msg.sender == minter, "2");
        IERC20 ERC20Instance = IERC20(tokenAddress);
        ERC20Instance.transfer(toAddress, amount);
    }
    
    function swapBntForEthTo(address bancorNetworkAddress, address payable to, uint256 minReturn) public payable override returns (uint256)
    {
        require (msg.sender == minter, "5");
        
        uint256 bntBalance = bntToken.balanceOf(address(this));
            
        // Allow
        uint256 allowance = bntToken.allowance(address(this), bancorNetworkAddress);
        if (allowance == 0)
        {
            bntToken.approve(bancorNetworkAddress, 2**256 - 1);
        }

        address[] memory exchangeBntToEthPath = new address[](3);
        exchangeBntToEthPath[0] = 0x1F573D6Fb3F13d689FF844B4cE37794d79a7FF1C;
        exchangeBntToEthPath[1] = 0xb1CD6e4153B2a390Cf00A6556b0fC1458C4A5533;
        exchangeBntToEthPath[2] = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;
        
        // Swap on bancor network
        IBancorNetwork bancorNetwork = IBancorNetwork(bancorNetworkAddress);
        return bancorNetwork.convertByPath2{value: 0}(exchangeBntToEthPath, bntBalance, minReturn, to);
    }
    
    function burnVortex(address bancorVortexAddress, IERC20[] calldata burnTokens) public override
    {
        require (msg.sender == minter, "4");

        // Get bancor vortex
        IBancorVortex bancorVortex = IBancorVortex(bancorVortexAddress);
            
        // Burn it
        bancorVortex.burn(burnTokens);
    }
    
    function burnVortexWithFlashbotBribe(address bancorNetworkAddress, address bancorVortexAddress, IERC20[] calldata burnTokens, uint256 bribePercentage)
                              payable external
    {
        burnVortex(bancorVortexAddress, burnTokens);

        // Convert BNT to ETH
        uint256 returnAmount = swapBntForEthTo(bancorNetworkAddress, address(0), 1);

        // Bribe 
        uint256 minerBribe = returnAmount * bribePercentage / 100;
        block.coinbase.transfer(minerBribe);
        
        // Return remainder
        msg.sender.transfer(returnAmount - minerBribe);
    }
}
