// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

interface cl {
    function latestAnswer() external view returns (int);
}

contract rKP3R {
    cl _kp3reth = cl(0xe7015CCb7E5F788B8c1010FC22343473EaaC3741);
    cl _eurusd = cl(0xb49f677943BC038e9857d61E7d053CaA2C1734C1);
    cl _ethusd = cl(0x5f4eC3Df9cbd43714FE2740f5E3616155c5b8419);

    function kp3reth() public view returns (uint) {
        return uint(_kp3reth.latestAnswer());
    }

    function kp3rusd() public view returns (uint) {
        return uint(_kp3reth.latestAnswer() * _ethusd.latestAnswer() / 1e18);
    }

    function kp3reur() public view returns (uint) {
        return kp3rusd() * 1e8 / uint(_eurusd.latestAnswer());
    }
}
