pragma solidity >0.6.0 <0.8.0;

import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/access/Ownable.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/token/ERC20/IERC20.sol";
import 'https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/interfaces/IUniswapV2Router02.sol';

contract Unifront is Ownable {


    // Address ropsten
    //address public constant weth = 0xc778417E063141139Fce010982780140Aa0cD5Ab;
    // Address mainnet
    address public constant weth = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;

    IUniswapV2Router02 private constant router = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);

    constructor() Ownable() payable  public {
    }

    receive() external payable {}

    function withdrawEther() public onlyOwner {
        msg.sender.transfer(address(this).balance);
    }

    function withdrawToken(address tokenAddress_) public onlyOwner {
        IERC20 tokenContract = IERC20(tokenAddress_);
        uint tokenAmount = tokenContract.balanceOf(address(this));
        tokenContract.transfer(owner(), tokenAmount);
    }

    function swapEthForToken(address tokenAddress_, uint weiAmount_, uint tokenMinAmount_) public onlyOwner {
        address[] memory path = new address[](2);
        path[0] = address(weth);
        path[1] = address(tokenAddress_);
        router.swapExactETHForTokens{value : weiAmount_}(tokenMinAmount_, path, address(this), block.timestamp);
    }

    function swapTokensForEth(address tokenAddress_, uint weiMinAmount_) public onlyOwner {
        IERC20 tokenContract = IERC20(tokenAddress_);
        uint tokenAmount = tokenContract.balanceOf(address(this));
        require(tokenContract.approve(address(router), tokenAmount), 'approve failed.');
        address[] memory path = new address[](2);
        path[0] = address(tokenAddress_);
        path[1] = address(weth);
        router.swapExactTokensForETH(tokenAmount, weiMinAmount_, path, address(this), block.timestamp);
    }


}

