pragma solidity >=0.6.0 <0.8.0;




contract OperatorAccess is AccessControl {
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");

    modifier onlyOperator {
        require(
            hasRole(OPERATOR_ROLE, _msgSender()),
            "Sender is not a operator"
        );
        _;
    }

    constructor() public {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(OPERATOR_ROLE, msg.sender);
    }

    function addOperator(address account) external {
        grantRole(OPERATOR_ROLE, account);
    }

    function renounceOperator(address account) external onlyOperator {
        renounceRole(OPERATOR_ROLE, account);
    }
}

interface IERCCore {
    function supportsInterface(bytes4 interfaceId) external view returns (bool);

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId
    ) external;
}

contract LecubeSale is ERC721Holder, OperatorAccess, Pausable {
    bytes4 public constant INTERFACE_ID_ERC721 = 0x80ac58cd;
    IERCCore public nftAddress;

    uint256 public cut;
    address payable public officialAddr;

    mapping(uint256 => Auction) public tokenIdToAuction;
    struct Auction {
        // Current owner of NFT
        address payable seller;
        // Price (in wei) at beginning of auction
        uint256 startingPrice;
        // Price (in wei) at end of auction
        uint256 endingPrice;
        // Duration (in seconds) of auction
        uint256 duration;
        // Time when auction started
        // NOTE: 0 if this auction has been concluded
        uint256 startedAt;
    }

    event AuctionCreated(
        uint256 tokenId,
        uint256 startingPrice,
        uint256 endingPrice,
        uint256 duration
    );
    event AuctionSuccessful(
        uint256 tokenId,
        uint256 totalPrice,
        address winner
    );
    event AuctionCancelled(uint256 tokenId);

    constructor(
        IERCCore _nftAddress,
        uint256 _cut,
        address payable _officialAddr
    ) public {
        require(
            _nftAddress.supportsInterface(INTERFACE_ID_ERC721),
            "is not 721 token"
        );
        nftAddress = _nftAddress;
        _setupRole(OPERATOR_ROLE, address(_nftAddress));
        cut = _cut;
        officialAddr = _officialAddr;
    }

    function _escrow(address _client, uint256 _tokenId) internal {
        nftAddress.safeTransferFrom(_client, address(this), _tokenId);
    }

    function _transfer(address _receiver, uint256 _tokenId) internal {
        nftAddress.safeTransferFrom(address(this), _receiver, _tokenId);
    }

    /// @dev Computes owner's cut of a sale.
    /// @param _price - Sale price of NFT.
    function _computeCut(uint256 _price) internal view returns (uint256) {
        return (_price * cut) / 10000;
    }

    /// @dev Returns current price of an NFT on auction. Broken into two
    function _currentPrice(Auction storage _auction)
        internal
        view
        returns (uint256)
    {
        uint256 secondsPassed = 0;

        // A bit of insurance against negative values (or wraparound).
        // Probably not necessary (since Ethereum guarnatees that the
        // now variable doesn't ever go backwards).
        if (block.timestamp > _auction.startedAt) {
            secondsPassed = block.timestamp - _auction.startedAt;
        }

        return
            _computeCurrentPrice(
                _auction.startingPrice,
                _auction.endingPrice,
                _auction.duration,
                secondsPassed
            );
    }

    /// @dev Computes the current price of an auction.
    function _computeCurrentPrice(
        uint256 _startingPrice,
        uint256 _endingPrice,
        uint256 _duration,
        uint256 _secondsPassed
    ) internal pure returns (uint256) {
        if (_secondsPassed >= _duration) {
            return _endingPrice;
        } else {
            int256 totalPriceChange = int256(_endingPrice) -
                int256(_startingPrice);

            // This multiplication can't overflow, _secondsPassed will easily fit within
            // 64-bits, and totalPriceChange will easily fit within 128-bits, their product
            // will always fit within 256-bits.
            int256 currentPriceChange = (totalPriceChange *
                int256(_secondsPassed)) / int256(_duration);

            // currentPriceChange can be negative, but if so, will have a magnitude
            // less that _startingPrice. Thus, this result will always end up positive.
            int256 currentPrice = int256(_startingPrice) + currentPriceChange;

            return uint256(currentPrice);
        }
    }

    function _addAuction(uint256 _tokenId, Auction memory _auction) internal {
        // Require that all auctions have a duration of
        // at least one minute. (Keeps our math from getting hairy!)
        require(_auction.duration >= 1 minutes);

        tokenIdToAuction[_tokenId] = _auction;

        emit AuctionCreated(
            uint256(_tokenId),
            uint256(_auction.startingPrice),
            uint256(_auction.endingPrice),
            uint256(_auction.duration)
        );
    }

    /// @dev Returns true if the NFT is on auction.
    /// @param _auction - Auction to check.
    function _isOnAuction(Auction storage _auction)
        internal
        view
        returns (bool)
    {
        return (_auction.startedAt > 0);
    }

    /// @dev Removes an auction from the list of open auctions.
    /// @param _tokenId - ID of NFT on auction.
    function _removeAuction(uint256 _tokenId) internal {
        delete tokenIdToAuction[_tokenId];
    }

    /// @dev Cancels an auction unconditionally.
    function _cancelAuction(uint256 _tokenId, address _seller) internal {
        _removeAuction(_tokenId);
        _transfer(_seller, _tokenId);
        emit AuctionCancelled(_tokenId);
    }

    /// @dev Creates and begins a new auction.
    /// @param _tokenId - ID of token to auction, sender must be owner.
    /// @param _startingPrice - Price of item (in wei) at beginning of auction.
    /// @param _endingPrice - Price of item (in wei) at end of auction.
    /// @param _duration - Length of auction (in seconds).
    /// @param _seller - Seller, if not the message sender
    function createOrder(
        uint256 _tokenId,
        uint256 _startingPrice,
        uint256 _endingPrice,
        uint256 _duration,
        address payable _seller
    ) external whenNotPaused onlyOperator returns (bool) {
        _escrow(_seller, _tokenId);
        Auction memory auction = Auction(
            _seller,
            _startingPrice,
            _endingPrice,
            _duration,
            uint256(block.timestamp)
        );
        _addAuction(_tokenId, auction);
    }

    //bid token with eth
    function bid(uint256 _tokenId)
        external
        payable
        whenNotPaused
        returns (bool)
    {
        Auction storage auction = tokenIdToAuction[_tokenId];
        require(_isOnAuction(auction));

        uint256 price = _currentPrice(auction);
        require(msg.value >= price, "money is not enough");

        address payable seller = auction.seller;

        _removeAuction(_tokenId);

        // Transfer proceeds to seller (if there are any!)
        if (price > 0) {
            uint256 auctioneerCut = _computeCut(price);
            uint256 sellerProceeds = price - auctioneerCut;
            seller.transfer(sellerProceeds);
        }
        uint256 bidExcess = msg.value - price;
        msg.sender.transfer(bidExcess);
        // emit the event AuctionSuccessful
        emit AuctionSuccessful(_tokenId, price, msg.sender);
        _transfer(msg.sender, _tokenId);
        return true;
    }

    function cancel(uint256 _tokenId) external whenNotPaused returns (bool) {
        Auction storage auction = tokenIdToAuction[_tokenId];
        require(_isOnAuction(auction));
        address seller = auction.seller;
        require(msg.sender == seller);
        _cancelAuction(_tokenId, seller);
        return true;
    }

    function cancelAuctionWhenPaused(uint256[] memory _tokenIds)
        whenPaused
        onlyOperator
        external
    {
        for(uint256 i = 0; i < _tokenIds.length; i ++){
            Auction storage auction = tokenIdToAuction[_tokenIds[i]];
            require(_isOnAuction(auction));
            _cancelAuction(_tokenIds[i], auction.seller);
        }
    }

    function withdrawBalance() external payable onlyOperator {
        officialAddr.transfer(address(this).balance);
    }

    function getPrice(uint256 _tokenId) external view returns (uint256) {
        Auction storage auction = tokenIdToAuction[_tokenId];
        require(_isOnAuction(auction));
        uint256 price = _currentPrice(auction);
        return price;
    }

    function setNftAddress(IERCCore _newNftAddress) external onlyOperator {
        nftAddress = _newNftAddress;
    }

    function setCut(uint256 _cut) external onlyOperator {
        cut = _cut;
    }

    function setOfficialAddr(address payable _officialAddr)
        external
        onlyOperator
    {
        officialAddr = _officialAddr;
    }
}
