// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface AggregatorV3Interface {
  function decimals() external view returns (uint8);

  function description() external view returns (string memory);

  function version() external view returns (uint256);

  // getRoundData and latestRoundData should both raise "No data present"
  // if they do not have data to report, instead of returning unset values
  // which could be misinterpreted as actual reported values.
  function getRoundData(uint80 _roundId)
    external
    view
    returns (
      uint80 roundId,
      int256 answer,
      uint256 startedAt,
      uint256 updatedAt,
      uint80 answeredInRound
    );

  function latestRoundData()
    external
    view
    returns (
      uint80 roundId,
      int256 answer,
      uint256 startedAt,
      uint256 updatedAt,
      uint80 answeredInRound
    );
}

contracts/SwapperContractV2.sol.content:
pragma solidity ^0.8.7;

import './AggregatorV3Interface.sol';

contract SwapperContractV2 {
  struct PackInfo {
    uint price;
    uint32 maxPerAddress;
  }
  
  address owner;
  AggregatorV3Interface internal priceFeed;
  mapping (string => PackInfo) public packIdToInfo;
  address withdrawalAddress;
  mapping (address => mapping(string => uint32)) public buyers;
  
  event IncomingTransaction(string memo, string packId, uint value, uint priceInEther, uint32 packsAmount, uint32 userTotalPacks);
  
  constructor(
      address _withdrawalAddress, 
      address _refAddress, 
      string memory _firstPackId, 
      uint _firstPackPrice, 
      uint32 _firstPackMaxPerAddress
  ) public {
    owner = msg.sender;
    withdrawalAddress = _withdrawalAddress;
    // Main - 0x5f4eC3Df9cbd43714FE2740f5E3616155c5b8419
    // Kovan - 0x9326BFA02ADD2366b30bacB125260Af641031331
    // Rinkeby - 0x8A753747A1Fa494EC906cE90E9f37563A8AF630e
    priceFeed = AggregatorV3Interface(_refAddress);
    if (bytes(_firstPackId).length > 1) {
        packIdToInfo[_firstPackId] = PackInfo(_firstPackPrice, _firstPackMaxPerAddress);
    }
  }
  
  function buy(string calldata _memo, string calldata _packId, uint32 _packsAmount) public payable {
    require(packIdToInfo[_packId].price > 0, "Wrong pack id");
    require(
      (_packsAmount <= packIdToInfo[_packId].maxPerAddress && _packsAmount > 0) && 
      buyers[msg.sender][_packId] + _packsAmount <= packIdToInfo[_packId].maxPerAddress, 
      "Wrong packs amount"
    );
    uint priceInEther = packPriceInEther(_packId);
    require(msg.value >= (priceInEther - priceInEther / 100) * _packsAmount, "Not enough value");
    
    buyers[msg.sender][_packId] = buyers[msg.sender][_packId] > 0 ? buyers[msg.sender][_packId] + _packsAmount : _packsAmount;
    emit IncomingTransaction(_memo, _packId, msg.value, priceInEther, _packsAmount, buyers[msg.sender][_packId]);
  }
  
  function packPriceInEther(string calldata packId) public view returns(uint price) {
    uint packPrice = packIdToInfo[packId].price;
    (,int ethRate,,,) = priceFeed.latestRoundData();
    price = packPrice * 1e18 / uint(ethRate) * 10**priceFeed.decimals();
  }
  
  function addPack(string calldata _id, uint _price, uint32 _maxPerAddress) public onlyOwner {
    packIdToInfo[_id] = PackInfo(_price, _maxPerAddress);
  }
  
  function withdraw() public onlyOwner {
    (bool sent,) = withdrawalAddress.call{value: address(this).balance}("");
    require(sent, "Failed to send Ether");
  }
  
  modifier onlyOwner() {
    require (owner == msg.sender, "Caller is not owner");
    _;
  }
}
