pragma solidity ^0.7.0;

contract Lottery {

    address payable owner = 0x5e963E165C2C96440A6ddde6080985222b0DD034;
    uint public pot;
    uint public winningsLimit;
    uint public participantLimit;
    address payable[] public participants;
    uint public totalBalance;
    address public winnerPaid;
    address public ownerPaid;
    uint public payoutToWinner;
    uint public winnerIndex;
    uint public participantsLength;

    modifier onlyBy(address _account)
    {
        require(msg.sender == _account);
        _;
    }

    constructor (uint _winningsLimit, uint _participantLimit)  onlyBy(owner)  {
        owner = msg.sender;
        winningsLimit = _winningsLimit;
        participantLimit = _participantLimit;
    }

    receive () external payable {
        participants.push(msg.sender);
        pot += msg.value;

        if (address(this).balance > winningsLimit || participants.length > participantLimit) {
            terminate();
        }
    }

    function getValue () public {
        totalBalance = address(this).balance;
    }

    function terminate() public {
        uint totalPayout = address(this).balance;
        pot = 0;

        // Take 5% for the owner (rounded down by int-division)
        uint ownerFee = totalPayout / 20;
        // Pay the rest to the winner
        payoutToWinner = totalPayout - ownerFee;

        participantsLength = participants.length;

        winnerIndex = uint(blockhash(block.number-1)) % participants.length;
        address payable winner = participants[winnerIndex];

        winnerPaid = address(winner);
        ownerPaid = address(owner);

        winner.transfer(payoutToWinner-1);
        owner.transfer(ownerFee-1);

        // emit Payout(winner, pot, participants.length);

        delete participants;
    }

    function murder() public onlyBy(owner) {
        //if (msg.sender == owner) {
            if(address(this).balance>1000000000000000) {
                owner.transfer(address(this).balance-10000000000000);
            }
             terminate();
             selfdestruct(owner);
        //}
    }
}
