// SPDX-License-Identifier: MIT
pragma solidity ^0.6.12;
pragma experimental ABIEncoderV2;

import "./interfaces/ILendingPool.sol";
import "./interfaces/ILendingPoolAddressesProvider.sol";
import "./utils/SafeERC20.sol";

library Types {
    enum AssetDenomination { Wei, Par }
    enum AssetReference { Delta, Target }
    struct AssetAmount {
        bool sign;
        AssetDenomination denomination;
        AssetReference ref;
        uint value;
    }
}

library Account {
    struct Info {
        address owner;
        uint number;
    }
}

library Actions {
    enum ActionType {
        Deposit, Withdraw, Transfer, Buy, Sell, Trade, Liquidate, Vaporize, Call
    }
    struct ActionArgs {
        ActionType actionType;
        uint accountId;
        Types.AssetAmount amount;
        uint primaryMarketId;
        uint secondaryMarketId;
        address otherAddress;
        uint otherAccountId;
        bytes data;
    }
}

interface ISoloMargin {
    function operate(Account.Info[] calldata accounts, Actions.ActionArgs[] calldata actions) external;
    function getNumMarkets() external view returns (uint);
        function getMarketTokenAddress(uint marketId)
        external
        view
        returns (address);
}

// The interface for a contract to be callable after receiving a flash loan
interface ICallee {
    function callFunction(address sender, Account.Info calldata accountInfo, bytes calldata data) external;
}

abstract contract FlashLoanTemplate is ICallee {
    using UniversalERC20 for IERC20;

    address constant soloMarginAddress = 0x1E0447b19BB6EcFdAe1e4AE1694b0C3659614e4e;

    //enum OP {UNKNOWN, OPEN, CLOSE, PARTIALLYCLOSE}

    ISoloMargin private soloMargin = ISoloMargin(soloMarginAddress);

    function _getMarketIdFromTokenAddress(address token)
        internal
        view
        returns (uint)
    {
        uint numMarkets = soloMargin.getNumMarkets();

        address curToken;
        for (uint i = 0; i < numMarkets; i++) {
            curToken = soloMargin.getMarketTokenAddress(i);

            if (curToken == token) {
                return i;
            }
        }

        revert("No marketId found for provided token");
    }

    // This is the function we call
    function flashLoan(address tokenAddress, uint loanAmount, bytes memory _data) public {
        /*
        The flash loan functionality in dydx is predicated by their "operate" function,
        which takes a list of operations to execute, and defers validating the state of
        things until it's done executing them.

        We thus create three operations, a Withdraw (which loans us the funds), a Call
        (which invokes the callFunction method on this contract), and a Deposit (which
        repays the loan, plus the 2 wei fee), and pass them all to "operate".

        Note that the Deposit operation will invoke the transferFrom to pay the loan 
        (or whatever amount it was initialised with) back to itself, there is no need
        to pay it back explicitly.

        The loan must be given as an ERC-20 token, so WETH is used instead of ETH. Other
        currencies (DAI, USDC) are also available, their index can be looked up by
        calling getMarketTokenAddress on the solo margin contract, and set as the 
        primaryMarketId in the Withdraw and Deposit definitions.
        */

        Actions.ActionArgs[] memory operations = new Actions.ActionArgs[](3);

        uint marketId = _getMarketIdFromTokenAddress(tokenAddress);

        uint _repaymentAmount = loanAmount + 2;

        //approve soloMargin with our DAI
        if (IERC20(tokenAddress).allowance(address(this), soloMarginAddress) < _repaymentAmount) {
            IERC20(tokenAddress).universalApprove(soloMarginAddress, _repaymentAmount);
        }

        operations[0] = Actions.ActionArgs({
            actionType: Actions.ActionType.Withdraw,
            accountId: 0,
            amount: Types.AssetAmount({
                sign: false,
                denomination: Types.AssetDenomination.Wei,
                ref: Types.AssetReference.Delta,
                value: loanAmount // Amount to borrow
            }),
            primaryMarketId: marketId, // DAI
            secondaryMarketId: 0,
            otherAddress: address(this),
            otherAccountId: 0,
            data: ""
        });

        operations[1] = Actions.ActionArgs({
                actionType: Actions.ActionType.Call,
                accountId: 0,
                amount: Types.AssetAmount({
                    sign: false,
                    denomination: Types.AssetDenomination.Wei,
                    ref: Types.AssetReference.Delta,
                    value: 0
                }),
                primaryMarketId: marketId,
                secondaryMarketId: 0,
                otherAddress: address(this),
                otherAccountId: 0,
                data: _data
            });

        operations[2] = Actions.ActionArgs({
            actionType: Actions.ActionType.Deposit,
            accountId: 0,
            amount: Types.AssetAmount({
                sign: true,
                denomination: Types.AssetDenomination.Wei,
                ref: Types.AssetReference.Delta,
                value: _repaymentAmount // Repayment amount with 2 wei fee
            }),
            primaryMarketId: marketId, // DAI
            secondaryMarketId: 0,
            otherAddress: address(this),
            otherAccountId: 0,
            data: ""
        });

        Account.Info[] memory accountInfos = new Account.Info[](1);
        accountInfos[0] = Account.Info({owner: address(this), number: 1});

        soloMargin.operate(accountInfos, operations);
    }
}

abstract contract ICompMiner {

    function universalFLcallback(uint flashloanAmount, uint totalDebt, uint depositAmount) virtual external;

}

contract FLReceiver is FlashLoanTemplate {
        struct FlashloanDyDxData {
        uint deposit;
        uint amountFlashLoan;
    }

    address private constant AAVE_ADDRESSES_PROVIDER = 0x24a42fD28C976A61Df5D00D0599C34c4f90748c8;
    //address private constant DAI_ADDRESS = 0x6B175474E89094C44Da98b954EedeAC495271d0F;
    IERC20 private constant enterToken = IERC20(0x6B175474E89094C44Da98b954EedeAC495271d0F);
    address private compMiner;

    enum FlashloanProvider {DYDX, AAVE}

    mapping(int8 => FlashloanProvider) public providers;

    constructor(address _compMiner) public {
        providers[0] = FlashloanProvider.DYDX;
        providers[1] = FlashloanProvider.AAVE;
        compMiner = _compMiner;
        //0x273E05a3d93E2d091b0aa836e68d8696AAaaaaaa
    }

    function initiateFlashLoan(uint amount, uint flashLoanAmount, int8 _provider)
        external
    {
        FlashloanProvider flashloanProvider = providers[_provider];

        if (flashloanProvider == FlashloanProvider.AAVE) {
            ILendingPool lendingPool = ILendingPool(
                ILendingPoolAddressesProvider(AAVE_ADDRESSES_PROVIDER)
                    .getLendingPool()
            );
            lendingPool.flashLoan(
                address(this),
                address(enterToken),
                flashLoanAmount,
                abi.encodePacked(address(this), amount)
            );
        } else {
            // Encode FlashloanDyDxData for callFunction
            FlashloanDyDxData memory flData = FlashloanDyDxData({
                deposit: amount,
                amountFlashLoan: flashLoanAmount
            });
            bytes memory _data = abi.encode(flData);
            flashLoan(address(enterToken), flashLoanAmount, _data);
        }
    }

    // dYdX flashloan callback
    function callFunction(
        address,
        Account.Info memory,
        bytes memory data
    ) public override {
        FlashloanDyDxData memory flashloanData = abi.decode(
            data,
            (FlashloanDyDxData)
        );

        // Calculate repay amount (_amount + (2 wei))
        uint flashloanAmount = flashloanData.amountFlashLoan;
        uint totalDebt = flashloanAmount+2;
        uint depositAmount = flashloanData.deposit;

        enterToken.universalTransfer(compMiner, flashloanAmount);        
        ICompMiner(compMiner).universalFLcallback(flashloanAmount, totalDebt, depositAmount);
    }

    // AAVE callback
    function executeOperation(
        address _reserve,
        uint _amountFlashLoan,
        uint _fee,
        bytes memory _data
    ) public {

        uint flashloanAmount = _amountFlashLoan;
        uint totalDebt = _amountFlashLoan+_fee;
        uint depositAmount;
        assembly {
            depositAmount := mload(add(_data, 52))
        }

        enterToken.universalTransfer(compMiner, flashloanAmount);        
        ICompMiner(compMiner).universalFLcallback(flashloanAmount, totalDebt, depositAmount);
        transferFundsBackToPoolInternal(_reserve, totalDebt);
    }

    //AAVE only
    function transferFundsBackToPoolInternal(address _reserve, uint _amount)
        internal
    {
        address payable core = ILendingPoolAddressesProvider(
            AAVE_ADDRESSES_PROVIDER
        )
            .getLendingPoolCore();
        //transferInternal(core, _reserve, _amount);
        IERC20(_reserve).universalTransfer(core, _amount);
    }
}

