pragma solidity >=0.7.0 <0.8.0;

/**
 * @title Storage
 * @dev Store & retrieve value in a variable
 */
contract PureChainArtTry {

    string svg;
    address owner;
    constructor(){
        owner = msg.sender;
    }

    modifier _ownerOnly(){
        require(msg.sender == owner);
        _;
    }

    function store(string memory mysvg) public _ownerOnly{
        svg = mysvg;
    }

    /**
     * @dev Return value 
     * @return value of 'number'
     */
    function retrieve() public view returns (string memory){
        return svg;
    }
}
