pragma solidity ^0.7.4;

contract UweBatchMint {
    string message;

    function setMessage(string memory _message) public {
        message=_message;
    }

    function batchMint(address[] calldata accounts, uint256[] calldata amounts) external
        returns (bool)

    {

        // Length should not be computed at each iteration
        uint accountCount = accounts.length;

        require(accountCount == amounts.length, "Input arrays must have the same length");

        message='Yeah man - Minting rocks.';
        return true;
    }
}
