// SPDX-License-Identifier: Unlicensed
pragma solidity 0.8.4;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";


contract BeaGood is ERC20, Ownable {
    /* Contract Variables */
    // Uniswap
    IUniswapV2Router02 public immutable uniswapRouter; 
    address public immutable uniswapPair;

    // Donation and Liquidity Fee
    //      50% of fee collected is added back to Liquidity Pool
    //      50% of fee collected is swapped and donated
    address payable private donationAddress;
    uint8 private feeDecimals;
    uint32 private feePercentage;
    uint128 private minTokensBeforeSwap;

    // State
    bool private inSwapDonateAddLiquidity;
    bool private swapDonateAddLiquidityEnabled;

    /* Events */
    event FeeUpdated(uint8 feeDecimals, uint32 feePercentage);
    event MinTokensBeforeSwapUpdated(uint128 minTokensBeforeSwap);
    event SwapAndDonateEnabledUpdated(bool enabled);
    event DonationAddrUpdated(address newAddress);
    event SwapDonateAddLiquidity(
        uint256 tokensSwapped,
        uint256 tokensAddedToLP,
        uint256 ethDonated,
        uint256 ethAddedToLP
    );

    /* Modifiers */
    modifier lockTheSwap {
        inSwapDonateAddLiquidity = true;
        _;
        inSwapDonateAddLiquidity = false;
    }

    constructor(IUniswapV2Router02 _uniswapRouter,
        address payable _donationAddress,
        uint8 _feeDecimals,
        uint32 _feePercentage,
        uint128 _minTokensBeforeSwap,
        bool _swapDonateAddLiquidityEnabled
    ) ERC20("BeaGood", "BEGD") {
        // Pre-mint total supply
        _mint(msg.sender, 50000000000 * 10 ** decimals());

        // Create a uniswap pool for this new token
        uniswapPair = IUniswapV2Factory(_uniswapRouter.factory())
            .createPair(address(this), _uniswapRouter.WETH());

        // Init uniswap v2 router
        uniswapRouter = _uniswapRouter;

        // Init donation vars
        donationAddress = _donationAddress;
        updateFee(_feeDecimals, _feePercentage);
        updateMinTokensBeforeSwap(_minTokensBeforeSwap);
        updateSwapDonateAddLiquidity(_swapDonateAddLiquidityEnabled);
    }

    function _transfer(address sender, address recipient, uint256 amount) internal override {
        // donate iff balance >= min balance required
        uint256 contractTokenBalance = balanceOf(address(this));
        bool overMinTokenBalance = contractTokenBalance >= minTokensBeforeSwap;

        // swap if all swap conditions met
        if (
            overMinTokenBalance &&
            !inSwapDonateAddLiquidity &&
            msg.sender != uniswapPair &&
            swapDonateAddLiquidityEnabled
        ) {
            swapDonateAddLiquidity(contractTokenBalance);
        }

        // get donation fee
        uint256 donationFee = calculateDonationFee(
            amount,
            feeDecimals,
            feePercentage
        );

        // get tokens after fee deducted
        uint256 tokensToTransfer = amount - donationFee;

        // send fee to this contract
        super._transfer(sender, address(this), donationFee);
        // send remaining tokens to recipient
        super._transfer(sender, recipient, tokensToTransfer);
    }

    function swapDonateAddLiquidity(uint256 contractTokenBalance) private lockTheSwap {
        // save a quarter of tokens to add to LP
        uint256 quarter = contractTokenBalance / 4;
        uint256 toSwap = contractTokenBalance - quarter;

        // capture balance before swap (will be donated, not part of LP)
        uint256 initialBalance = address(this).balance;

        // swap contract BEGD balance for ETH
        swapBEGDForETH(toSwap);

        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance;
        require(newBalance > 0, "No balance after swap.");
        require(newBalance - initialBalance > 0, "ETH balance less than before swap.");

        // calculate ETH for donation and liquidity
        uint256 lpETH = (newBalance - initialBalance) / 3;
        uint256 donationETH = newBalance - lpETH;

        // donate contract balance
        donateEth(donationETH);

        // add liquidity
        addLiquidity(quarter, lpETH);

        // emit event
        emit SwapDonateAddLiquidity(toSwap, quarter, donationETH, lpETH);
    }

    function swapBEGDForETH(uint256 amount) private {
        // generate path
        address [] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapRouter.WETH();

        // approve
        _approve(address(this), address(uniswapRouter), amount);

        // swap
        uniswapRouter.swapExactTokensForETHSupportingFeeOnTransferTokens(
            amount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    function donateEth(uint256 ethAmount) private {
        // perform transfer of eth to charity wallet
        (bool sent,) = donationAddress.call{value: ethAmount}("");
        require(sent, "Failed to donate Ether");
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapRouter), tokenAmount);

        // add the liquidity
        uniswapRouter.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(this),
            block.timestamp
        );
    }

    function calculateDonationFee(
        uint256 _amount,
        uint8 _feeDecimals,
        uint32 _feePercentage
    ) public pure returns (uint256 fee) {
        fee = _amount * _feePercentage / (10**(uint256(_feeDecimals) + 2));
    }

    receive() payable external {}

    /* Owner maintenance methods */
    function updateFee(uint8 _feeDecimals, uint32 _feePercentage) public onlyOwner
    {
        // Modify fee
        // e.x. 
        //    1% fee   === 0 fee decimals, and 1 fee percent
        //    0.1% fee === 1 fee decimals, and 1 fee percent
        feeDecimals = _feeDecimals;
        feePercentage = _feePercentage;
        emit FeeUpdated(_feeDecimals, _feePercentage);
    }

    function updateMinTokensBeforeSwap(uint128 _minTokensBeforeSwap) public onlyOwner
    {
        // Update target balance before a swap
        minTokensBeforeSwap = _minTokensBeforeSwap;
        emit MinTokensBeforeSwapUpdated(_minTokensBeforeSwap);
    }

    function updateSwapDonateAddLiquidity(bool _enabled) public onlyOwner {
        // Update swap and donate
        swapDonateAddLiquidityEnabled = _enabled;
        emit SwapAndDonateEnabledUpdated(_enabled);
    }

    function updateDonationAddress(address payable newAddress) public onlyOwner {
        require(newAddress != donationAddress, "New address cannot match current charity");

        // Stop swap and donation
        updateSwapDonateAddLiquidity(false);

        // Update address
        donationAddress = newAddress;

        // Resume swap and donation
        updateSwapDonateAddLiquidity(true);

        // Emit event
        emit DonationAddrUpdated(newAddress);
    }
}
