pragma solidity >=0.4.0 <0.8.0;

/**
 * @title VerumCertificateSimple
 * @dev Store certificate information from Verum Digital
 */
contract VerumCertificateSimple {
    mapping(string => string) certificates;

    function newCertificate(string memory content, string memory key) public {
        certificates[key] = content;
    }

    function getCertificate(string memory key) public view returns (string memory) {
        return (certificates[key]);
    }
}
