pragma solidity ^0.6.0;

contract Send {
    function multisendEther(address[] calldata _contributors, uint256[] calldata _balances) external payable {
        require(_contributors.length == _balances.length);
        uint256 i = 0;
        for (i; i < _contributors.length; i++) {
            address payable wallet = payable(_contributors[i]);
            wallet.transfer(_balances[i]);
        }
    }   
}
