pragma solidity =0.6.6;

interface IERC20 {
    event Approval(address indexed owner, address indexed spender, uint value);
    event Transfer(address indexed from, address indexed to, uint value);

    function name() external view returns (string memory);
    function symbol() external view returns (string memory);
    function decimals() external view returns (uint8);
    function totalSupply() external view returns (uint);
    function balanceOf(address owner) external view returns (uint);
    function allowance(address owner, address spender) external view returns (uint);

    function approve(address spender, uint value) external returns (bool);
    function transfer(address to, uint value) external returns (bool);
    function transferFrom(address from, address to, uint value) external returns (bool);
}

interface ITransferEmitter {

}

contract FakeERC20 {
    event Transfer(address indexed from, address indexed to, uint value);

    string private _name;
    string private _symbol;
    uint8 private _decimals;

    constructor () public {
        _name = "Fake";
        _symbol = "FU";
        _decimals = 18;
    }

    function name() external view returns (string memory) { return _name; }
    function symbol() external view returns (string memory) {return _symbol; }
    function decimals() external view returns (uint8) { return _decimals; }

    function fakeEmit (
        address from,
        address to,
        uint value
    ) public {
        emit Transfer(from, to, value);
    }
}

/**
 * This contract will always transfer the `amt` of an IERC20
 * token to the caller... regardless of who the caller is.
 * 
 * Its goal is to test to see if people will blindly
 * front run things. i.e. how arbitrary the Dark Forest is
 */
contract Trap {
    address public owner;
    // Default to USDC
    address public tokenContract = 0xA0b86991c6218b36c1d19D4a2e9Eb0cE3606eB48;
    uint public amt = 1;
    uint public decimals = 6;

    constructor() public {
        owner = msg.sender;
    }

    modifier isOwner() {
        require(msg.sender == owner, "Caller is not owner");
        _;
    }

    /**
     * Transfer the `amt` of the ERC20 tokens to the caller
     * the objective would be to call this function and see
     * if someone notices that it sends the caller funds. It
     * takes two arguments, these arguments do nothing but
     * test to see if the frontrunner will include those
     * arguments.
     */
    function go (
        uint value1,
        uint value2
    ) public {
        IERC20 token = IERC20(tokenContract);
        token.transfer(msg.sender, (amt * 10**decimals));
    }

    /**
     * Update the amount of ERC20 token that calling 
     * the go function would transfer to the caller.
     */
    function setAmt (uint _amt) public isOwner {
        amt = _amt;
    }

    /**
     * Set the decimals that the ERC20 contract uses, this
     * could be done programmatically but...
     */
    function setDecimals(uint _decimals) public isOwner {
        decimals = _decimals;
    }

    /**
     * Update the contract address that the ERC20 token is
     * deployed to.
     */
    function setTokenContract (address _newToken) public isOwner {
        tokenContract = _newToken;
    }

    function destroy () public isOwner {
        selfdestruct(payable(owner));
    }
}
