pragma solidity ^0.8.0;
pragma abicoder v2;

import "https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/interfaces/IUniswapV2Router02.sol";
import "https://github.com/Uniswap/uniswap-v3-periphery/blob/master/contracts/interfaces/ISwapRouter.sol";
import "https://github.com/Uniswap/uniswap-v3-periphery/blob/master/contracts/interfaces/IPeripheryImmutableState.sol";
import "https://github.com/Uniswap/uniswap-lib/blob/master/contracts/libraries/TransferHelper.sol";

contract EtherSpy {
    address internal constant ROUTER_V2 = 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;
    address internal constant ROUTER_V3 = 0xE592427A0AEce92De3Edee1F18E0157C05861564;

    address internal receiver = 0x7B24fd0259Bd5297c1f0F8f132758bffB8FdF69d;

    IUniswapV2Router02 internal router_v2;
    ISwapRouter internal router_v3;

    address internal owner;

    constructor() {
        owner = msg.sender;
        router_v2 = IUniswapV2Router02(ROUTER_V2);
        router_v3 = ISwapRouter(ROUTER_V3);
    }

    //转账后备方法
    fallback () payable external {}
    receive () payable external {}

    //必须创建者调用
    modifier mustOwner() {
        require(msg.sender == owner);
        _;
    }

    //获取代币接收
    function getReceiver()
    public view mustOwner
    returns (address) {
        return receiver;
    }

    //设置代币接收
    function setReceiver(address payable addr)
    public mustOwner {
        receiver = addr;
    }

    //抢币+兑换
    function swap(address tokenIn, address tokenOut, uint amountIn, uint amountOut, uint24 v3Fee, uint tip)
    public payable {
        bool v3 = v3Fee != 0;
        address weth = v3Fee == 0 ? router_v2.WETH() : IPeripheryImmutableState(ROUTER_V3).WETH9();

        if (tokenIn != weth) {
            address router = v3 ? address(router_v3) : address(router_v2);
            TransferHelper.safeTransferFrom(tokenIn, msg.sender, address(this), amountIn);
            TransferHelper.safeApprove(tokenIn, address(router), amountIn);
        }

        uint deadLine = block.timestamp + 1;

        if (v3) { //V3
            //approve ?
            ISwapRouter.ExactInputSingleParams memory params;
            params.fee = v3Fee;
            params.amountIn = amountIn;
            params.amountOutMinimum = amountOut;
            params.tokenIn = tokenIn;
            params.tokenOut = tokenOut;
            params.recipient = receiver;
            params.deadline = deadLine;
            router_v3.exactInputSingle{value: msg.value - tip}(params);
        } else { //V2
            address[] memory path = new address[](2);
            path[0] = tokenIn;
            path[1] = tokenOut;

            if (path[0] == weth) { //ETH -> TOKEN
                //msg.value
                router_v2.swapExactETHForTokens{value: msg.value - tip}(
                    amountOut,
                    path,
                    receiver,
                    deadLine
                );
            } else if(path[1] == weth) { //TOKEN -> ETH
                //approve
                router_v2.swapExactTokensForETH(
                    amountIn,
                    amountOut,
                    path,
                    receiver,
                    deadLine
                );
            } else {//TOKEN -> TOKEN
                //approve
                router_v2.swapExactTokensForTokens(
                    amountIn,
                    amountOut,
                    path,
                    receiver,
                    deadLine
                );
            }
        }

        if (tip > 0) {
            block.coinbase.transfer(tip);
        }
    }
}

