// SPDX-License-Identifier: MIT LICENSE
pragma solidity 0.8.4;

interface Metro {
    function mintNFT(uint256 amount, bool stake) external payable;
    function totalSupply() external returns (uint);
    function safeTransferFrom(address from, address to, uint256 tokenId) external;
}

// address SHRYDER = 0x4642D9D9A434134CB005222eA1422e1820508d7B;
contract Child {
    constructor (address nft_address, address payable shryder, uint totalSupply) payable {
        Metro(nft_address).mintNFT{value: 0.2 ether}(2, false);

        Metro(nft_address).safeTransferFrom(address(this), shryder, totalSupply);
        Metro(nft_address).safeTransferFrom(address(this), shryder, totalSupply + 1);

        selfdestruct(shryder);
    }
}

contract Dad {
    function lol (uint amount, address nft_address) external payable {
        require(msg.sender == 0x4642D9D9A434134CB005222eA1422e1820508d7B);

        uint totalSupply = Metro(nft_address).totalSupply() + 1;
        for (uint i = 0; i < amount; i++) {
            new Child{value: 0.2 ether}(nft_address, payable(msg.sender), totalSupply);
            totalSupply += 2;
        }
    }
}
