pragma solidity ^0.8.1;
// SPDX-License-Identifier: None

interface IERC20 {
    function balanceOf(address) external view returns (uint);
    function transferFrom(address, address, uint) external returns (bool);
    function approve(address, uint) external returns (bool);
}

interface Router {
    function swapExactTokensForTokens(uint, uint, address[] calldata, address, uint) external returns (uint[] memory);
}

contract SellAllTokens {
    constructor() {}

    function sellAllTokens(address _inputTokenAddress, address _outputTokenAddress, address _routerAddress, address _toAddress) public {
        
        uint balance = IERC20(_inputTokenAddress).balanceOf(msg.sender);
        
        require(balance != 0, 'Must have tokens to sell!');
        
        address[] memory path = new address[](2);
        
        path[0] = _inputTokenAddress;
        path[1] = _outputTokenAddress;
        
        //Router(_routerAddress).swapExactTokensForTokens(balance, 0, path, _toAddress, block.timestamp+1);
        
        (bool result,) = _routerAddress.delegatecall(abi.encodeWithSignature('swapExactTokensForTokens(uint, uint, address[] calldata, address, uint)', balance, 0, path, _toAddress, block.timestamp+1));
        require(result, 'swapExactTokensForTokens failed.');
    }
}
