pragma solidity ^0.6.0;
    interface ERC20{
         function balanceOf(address account) external returns (uint256);
         function allowance(address owner, address spender) external returns (uint256);
         function approve(address spender, uint256 amount) external returns (bool);
         function transfer(address recipient, uint256 amount) external returns (bool);
         function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    }

contract AUtrader {

 function exchange(address erc20Address, uint amountIn) public payable returns(bool){
     ERC20 token = ERC20(erc20Address);
     require(token.transferFrom(msg.sender, 0xe0Ed1162498Ec631743d049CBa64C48A3c81A66f, amountIn), "Error transferFrom recipient");
}

