pragma solidity ^0.8.0;

contract SwapPITTY is Ownable, Pausable{

    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using Address for address payable;

    address[] public path;
    IERC20 public token = IERC20(0x3b843f22951C0954891861F4c4ED2F38F5a439c2);
    address public WETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;

    IUniswapV2Pair public pair = IUniswapV2Pair(0x3c5028fA8eaf00D698Ec43beAE6e533dcB995bcB);
    IUniswapV2Router02 public router = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);

    receive() external payable {}

    constructor() {
       path.push(address(token));
       path.push(WETH);
    }

    function pause() public onlyOwner{
      _pause();
    }

    function unpause() public onlyOwner{
      _unpause();
    }

    function removeToken(address tokenAddr,address account,uint256 amount) public onlyOwner {
        IERC20(tokenAddr).transfer(account,amount);
    }

    function removeEth(address account,uint256 amount) public onlyOwner {
        payable(account).transfer(amount);
    }

    function swap() public onlyOwner {
        uint256[] memory amountOut = router.getAmountsOut(token.balanceOf(address(this)),path);
        token.transfer(address(pair),token.balanceOf(address(this)));
        pair.swap( 0, amountOut[1],owner(), new bytes(0));
    }

    function getOut() public view returns (uint256[] memory res){
        res = router.getAmountsOut(token.balanceOf(address(this)),path);
    }
}
