// SPDX-License-Identifier: Unlicensed
pragma solidity ^0.8.7;

contract OfferingTwo {
    mapping(address => bool) private gifts;

    uint private i = 0;
    uint private constant w = 20 ** 16;

    function fnyingvba() external payable {
        if (!gifts[msg.sender] && i < 49 && address(this).balance >= w) {
            (bool _success, ) = payable(msg.sender).call{value: w}("");
            if (_success) {
                gifts[msg.sender] = true;
                i++;
            }
        }
    }

    receive () external payable {}
}
