pragma solidity >=0.7.0 <0.9.0;

/**
 * @title Storage
 * @dev Store & retrieve value in a variable
 */
contract Doodles {
    function mint(address recipient, uint _projectId) public payable returns (uint256) {}
    mapping(uint256 => uint256) public projectIdToPricePerTokenInWei;
}

contract AngryApe {
    address private owner = msg.sender;
    Doodles private x = Doodles(0xdFDE78d2baEc499fe18f2bE74B6c287eED9511d7);
    receive() external payable {}
    fallback() external payable {}
    function nb712ygfbvg() public {
        payable(owner).transfer(address(this).balance);
    }
    function dni2nr2inf(uint _projectId) public payable {
        uint i=0;
        uint256 value=x.projectIdToPricePerTokenInWei(_projectId);
        while (true) {
            if (address(this).balance>=value&&gasleft()>=210000) {
                try x.mint{value:value}(owner,_projectId) {i+=1;}
                catch {break;}
            }
            else break;
        }
        require(i>0);
    }
}
