pragma solidity ^0.6.6;

import "https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/libraries/UniswapV2Library.sol";
import "https://github.com/Uniswap/uniswap-v2-periphery/blob/master/contracts/interfaces/IUniswapV2Router02.sol";
//import "https://github.com/Uniswap/uniswap-v2-core/blob/master/contracts/interfaces/IUniswapV2Pair.sol";
import "https://github.com/Uniswap/uniswap-v2-core/blob/master/contracts/interfaces/IUniswapV2Factory.sol";
import "https://github.com/Uniswap/uniswap-v2-core/blob/master/contracts/interfaces/IERC20.sol";

contract Arbitrage {
    address public factory;
    uint constant deadline = 10 days;
    IUniswapV2Router02 public sushiRouter;
    
    constructor(address _factory, address _sushiRouter) public {
        // 0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f, 0xd9e1cE17f2641f24aE83637ab66a2cca9C378B9F
        factory = _factory;  
        sushiRouter = IUniswapV2Router02(_sushiRouter);
    }
    
    function startArbitrage(
        address token0, 
        address token1, 
        uint amount0, 
        uint amount1
    ) external {
        address pairAddress = IUniswapV2Factory(factory).getPair(token0, token1); // The address of the pair smart contract of UniSwap
        require(pairAddress != address(0), 'There is no such pool');
        IUniswapV2Pair(pairAddress).swap(
            amount0, 
            amount1, 
            address(this),
            bytes('not empty')
        );
    }
    
    function uniswapV2Call(
        address _sender, // The address that triggered the Flash Loan
        uint _amount0, // The amount borrowed
        uint _amount1, 
        bytes calldata _data
    ) external {
        address[] memory path = new address[](2); // An array of addresses used to complete the trade.
        uint amountToken = _amount0 == 0 ? _amount1 : _amount0; // This will be equal to “_amount0”.
        
        address token0 = IUniswapV2Pair(msg.sender).token0(); // The address of the first token in Uniswap’s liquidity pool.
        address token1 = IUniswapV2Pair(msg.sender).token1(); // The address of the second token in Uniswap’s liquidity pool.
    
        require(
            msg.sender == UniswapV2Library.pairFor(factory, token0, token1), 
            'Unauthorized'
        ); 
        require(_amount0 == 0 || _amount1 == 0);
        
        path[0] = _amount0 == 0 ? token1 : token0; // Sell token1 for token0 on Sushiswap
        path[1] = _amount0 == 0 ? token0 : token1;
        
        IERC20 token = IERC20(_amount0 == 0 ? token1 : token0); // A pointer to the token that we are going to sell on SushiSwap.
        
        token.approve(address(sushiRouter), amountToken); // Allow router to spend token
        
        uint amountRequired = UniswapV2Library.getAmountsIn(
            factory, 
            amountToken, 
            path
        )[0]; // The amount of the initial token that must be reimburshed for the Flash Loan to UniSwap.
        
        uint amountReceived = sushiRouter.swapExactTokensForTokens(
            amountToken, // Amount to sell
            amountRequired, // Min amount
            path, 
            msg.sender, 
            deadline
        )[1]; // Sell the token borrow from Uniswap on Sushiswap
        
        IERC20 otherToken = IERC20(_amount0 == 0 ? token0 : token1); // Pointer of other token from Sushiswap
        otherToken.transfer(msg.sender, amountRequired); //Reimbursh Loan
        otherToken.transfer(tx.origin, amountReceived - amountRequired); //Keep Profit
    }
}

