// SPDX-License-Identifier: Sam and Auto
pragma solidity ^0.6.6;

import '@uniswap/v2-periphery/contracts/libraries/UniswapV2Library.sol';
import '@uniswap/v2-core/contracts/interfaces/IUniswapV2Callee.sol';
import '@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol';

interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

contract BorrowBot {
  address constant limitOrder = 0x3ef51736315F52d568D6D2cf289419b9CfffE782;
  address constant uniFactory = 0x5C69bEe701ef814a2B6a3EDD4B1652CB9cc5aA6f;
  address constant sushiFactory = 0xC0AEe478e3658e2610c5F7A4A2E1777cE9e4f2Ac;

  function start(
    address pairAddress,
    uint256 amount0,
    uint256 amount1,
    bytes calldata _data) external {

    IUniswapV2Pair(pairAddress).swap(
      amount0,
      amount1,
      address(this),
      _data
    );
  }

  function uniswapV2Call(
    address _sender,
    uint _amount0,
    uint _amount1,
    bytes calldata _data
  ) external {
    address[] memory path = new address[](2);
    uint amountToken = _amount0 == 0 ? _amount1 : _amount0;

    address token0 = IUniswapV2Pair(msg.sender).token0();
    address token1 = IUniswapV2Pair(msg.sender).token1();

    require(
      msg.sender == UniswapV2Library.pairFor(uniFactory, token0, token1) ||
      msg.sender == UniswapV2Library.pairFor(sushiFactory, token0, token1),
      'Unauthorized'
    );
    require(_amount0 == 0 || _amount1 == 0);

    path[0] = _amount0 == 0 ? token1 : token0;
    path[1] = _amount0 == 0 ? token0 : token1;

    IERC20 borrowedToken = IERC20(_amount0 == 0 ? token1 : token0);

    borrowedToken.approve(address(limitOrder), amountToken);
    limitOrder.call(_data);

    uint loanRepaymentAmount;
    if (msg.sender == UniswapV2Library.pairFor(uniFactory, token0, token1)) {
      loanRepaymentAmount = UniswapV2Library.getAmountsIn(
        uniFactory,
        amountToken,
        path
      )[0];
    } else {
      loanRepaymentAmount = UniswapV2Library.getAmountsIn(
        sushiFactory,
        amountToken,
        path
      )[0];
    }

    IERC20 otherToken = IERC20(_amount0 == 0 ? token0 : token1);
    uint amountReceived = otherToken.balanceOf(address(this));
    require(amountReceived > loanRepaymentAmount);

    otherToken.transfer(msg.sender, loanRepaymentAmount);

    otherToken.transfer(tx.origin, amountReceived - loanRepaymentAmount);
  }

}
