pragma solidity ^0.8.7;

import "./Foundation.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

abstract contract Foundation_Impl is Foundation, Ownable, Initializable {
    using Address for address;
    using Strings for uint256;
    using SafeMath for uint256;
    using SafeMath for uint8;

    uint256 public override fees = 0.1 ether;

    bool /*public override */contractPaused = false;

    address constant RESERVE_ADDR = address(3);
    address constant PROFITS_ADDR = address(6);
    address constant DEPOSIT_ADDR = address(9);

    function isAddressAdmin(address sender) override virtual public view returns (bool);

    modifier isAdmin() virtual {
        require(isAddressAdmin(msg.sender), "403");
        _;
    }

    function circuitBreaker() override public isAdmin { // onlyOwner can call
        if (!contractPaused) { contractPaused = true; }
        else { contractPaused = false; }
    }
    // If the contract is paused, stop the modified function
    // Attach this modifier to all public functions
    modifier checkIfPaused() {
        require(!contractPaused || isAddressAdmin(msg.sender), "405");
        _;
    }

    modifier strlen(string memory str, uint256 max) {
        require(bytes(str).length <= max, "429");
        _;
    }

    uint8 public maxNameLength = 16;
    function setMaxNameLength(uint8 _max) public isAdmin {
        maxNameLength = _max;
    }

    function close() override virtual public onlyOwner {
        (bool success, ) = payable(owner()).call{value: address(this).balance}('');
        require(success, "");
    }

    receive() virtual external payable {
    }

    fallback() external payable {

    }
}

