// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.7;

import "../base/DS.sol";
import "../base/Foundation_Impl.sol";
import "./EternalStore.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import "./RIStore.sol";
import "../rauction/Rauction.sol";
import "../ions/I0NS.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract EternalStore_Impl is Foundation_Impl, EternalStore, ReentrancyGuard, ERC721Holder {
  RIStore ristore;
  I0NS i0ns;
  Rauction rauction;

  address public override beneficiary;
  mapping(address => uint) public override accountBalances;
  mapping(uint256 => DS.AuctionItem) auctionItems;
  mapping(address => uint[]) ownerTokens;
  mapping(uint256 => DS.TokenMeta) tokenMeta;

  mapping(bytes32 => uint) public override uIntStorage;
  mapping(bytes32 => string) public override stringStorage;
  mapping(bytes32 => address) public override addressStorage;
  mapping(bytes32 => bytes) public override bytesStorage;
  mapping(bytes32 => bool) public override boolStorage;
  mapping(bytes32 => int) public override intStorage;

  function init(RIStore _ristore, I0NS _i0ns, Rauction _rauction) public initializer {
    boolStorage[keccak256(abi.encodePacked("admins", owner()))] = true;
    updateRole(owner(), true, true);

    ristore = _ristore;
    i0ns = _i0ns;
    rauction = _rauction;

    setContractAddress(DS.ContractNames.EStore, address(this));
    setContractAddress(DS.ContractNames.RIStore, address(ristore));
    setContractAddress(DS.ContractNames.I0NS, address(i0ns));
    setContractAddress(DS.ContractNames.Rauction, address(rauction));

    // Reserve ID 0 to 96310
    ristore.init(this, i0ns, rauction, 96311);
    i0ns.init(this, ristore, rauction);
    rauction.init(this, ristore, i0ns);

    beneficiary = owner();
  }

  function contractAddress(DS.ContractNames _name) override public view returns (address) {
    return addressStorage[keccak256(abi.encodePacked("contract", _name))];
  }
  function minters(address m) override public view returns (bool) {
    return boolStorage[keccak256(abi.encodePacked("minters", m))];
  }
  function isAddressAdmin(address sender) override(Foundation, Foundation_Impl) public view returns (bool) {
    return sender == address(this) || sender == owner() || boolStorage[keccak256(abi.encodePacked("admins", sender))];
  }
  function updateRole(address roler, bool status, bool isMinter) override public isAdmin {
    bytes32 key = keccak256(abi.encodePacked((isMinter ? "minters" : "admins"), roler));
    if (status) boolStorage[key] = true;
    else delete boolStorage[key];
  }
  function setContractAddress(DS.ContractNames _name, address _addr) override public isAdmin {
    bytes32 key = keccak256(abi.encodePacked("contract", _name));
    if (addressStorage[key] != address(0)) updateRole(addressStorage[key], false, false);
    addressStorage[key] = _addr;
    updateRole(_addr, true, false);
  }

  function changeBeneficiary(address _beneficiary) public isAdmin {
    transferAccountBalance(beneficiary, _beneficiary, 0);
    beneficiary = _beneficiary;
  }

  function updateAccountBalance(address _to, uint256 val, bool add) override public payable isAdmin {
    //        address to = (_to == address(0) ? beneficiary : _to);
    if (add) accountBalances[_to] += val;
    else accountBalances[_to] -= val;
    emit BalanceUpdated(_to, val, add);
  }

  function transferAccountBalance(address _from, address _to, uint256 _val) override public isAdmin {
    uint256 val = (_val == 0 ? accountBalances[_from] : _val);
    //        address to = (_to == address(0) ? beneficiary : _to);
    accountBalances[_from] -= val;
    accountBalances[_to] += val;
    emit BalanceTransferred(_from, _to, _val);
  }

  /// Collect your account balance
  function collect() override public checkIfPaused nonReentrant returns (bool success) {
    uint256 amount = accountBalances[msg.sender];
    success = false;
    if (amount > 0) {
      accountBalances[msg.sender] = 0;
      (success, ) = payable(msg.sender).call{value:amount}('');

      if (success) emit BalanceCollected(msg.sender, amount);
      else accountBalances[msg.sender] = amount;
    }
  }

  function getTokenMeta(uint256 tokenId) override public view returns (DS.TokenMeta memory) {
    return tokenMeta[tokenId];
  }
  function getAuctionItem(uint256 tokenId, bool check) override public view returns (DS.AuctionItem memory) {
    DS.AuctionItem memory item = auctionItems[tokenId];
    if (check) require(item.valid, "404");
    return item;
  }
  function updateTokenMeta(uint256 tokenId, DS.TokenMeta memory meta) override public isAdmin {
    tokenMeta[tokenId] = meta;
  }
  function updateAuctionItem(uint256 tokenId, DS.AuctionItem memory item) override public isAdmin {
    auctionItems[tokenId] = item;
  }
  function deleteTokenMeta(uint256 tokenId) override public isAdmin {
    delete tokenMeta[tokenId];
  }
  function deleteAuctionItem(uint256 tokenId) override public isAdmin {
    delete auctionItems[tokenId];
  }

  function tokenOwners(uint256 id) override public view returns (address) {
    return addressStorage[keccak256(abi.encodePacked("tokenOwner", id))];
  }
  function listOwnerTokens(address owner) override public view returns (uint256[] memory) {
    return ownerTokens[owner];
  }
  function modOwnerTokens(address owner, uint256 i, bool push) override isAdmin checkIfPaused public {
    uint256[] storage tokens = ownerTokens[owner];
    if (push) tokens.push(i);
    else {
      tokens[i] = tokens[tokens.length - 1];
      tokens.pop();
    }

  }

  function xTransfer(uint256 tokenId, address to) override public isAdmin checkIfPaused {
    DS.TokenMeta memory meta = tokenMeta[tokenId];
    require(meta.valid && meta.xNft.valid, "484");
    try IERC721(meta.xNft.xContract).safeTransferFrom(address(this), to, meta.xNft.xTokenId) {
      return;
    } catch (bytes memory) { revert("481"); }
  }


  function setUint(bytes32 _key, uint256 _value) override isAdmin checkIfPaused public {
    uIntStorage[_key] = _value;
  }

  function setString(bytes32 _key, string calldata _value) override isAdmin checkIfPaused public {
    stringStorage[_key] = _value;
  }

  function setAddress(bytes32 _key, address _value) override isAdmin checkIfPaused public {
    addressStorage[_key] = _value;
  }

  function setBytes(bytes32 _key, bytes calldata _value) override isAdmin checkIfPaused public {
    bytesStorage[_key] = _value;
  }

  function setBool(bytes32 _key, bool _value) override isAdmin checkIfPaused public {
    boolStorage[_key] = _value;
  }

  function setInt(bytes32 _key, int _value) override isAdmin checkIfPaused public {
    intStorage[_key] = _value;
  }

  function deleteUint(bytes32 _key) override isAdmin checkIfPaused public {
    delete uIntStorage[_key];
  }

  function deleteString(bytes32 _key) override isAdmin checkIfPaused public {
    delete stringStorage[_key];
  }

  function deleteAddress(bytes32 _key) override isAdmin checkIfPaused public {
    delete addressStorage[_key];
  }

  function deleteBytes(bytes32 _key) override isAdmin checkIfPaused public {
    delete bytesStorage[_key];
  }

  function deleteBool(bytes32 _key) override isAdmin checkIfPaused public {
    delete boolStorage[_key];
  }

  function deleteInt(bytes32 _key) override isAdmin checkIfPaused public {
    delete intStorage[_key];
  }

  function upgradeI0ns(I0NS upgraded) public onlyOwner {
    upgraded.init(this, ristore, rauction);
    rauction.setI0NS(upgraded);
    ristore.setI0NS(upgraded);
    setContractAddress(DS.ContractNames.I0NS, address(upgraded));
    i0ns.circuitBreaker();
    i0ns = upgraded;
  }
  function upgradeRauction(Rauction upgraded) public onlyOwner {
    upgraded.init(this, ristore, i0ns);
    i0ns.setRauction(upgraded);
    ristore.setRauction(upgraded);
    setContractAddress(DS.ContractNames.Rauction, address(upgraded));
    rauction.circuitBreaker();
    rauction = upgraded;
  }
  function upgradeRIStore(RIStore upgraded) public onlyOwner {
    upgraded.init(this, i0ns, rauction, ristore.getCounter());
    i0ns.setRIStore(upgraded);
    rauction.setRIStore(upgraded);
    setContractAddress(DS.ContractNames.RIStore, address(upgraded));
    ristore.circuitBreaker();
    ristore = upgraded;
  }
}

