pragma solidity =0.6.6;

import "@uniswap/v2-periphery/contracts/interfaces/IERC20.sol";
import "@uniswap/v2-periphery/contracts/libraries/SafeMath.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IWETH.sol";
import "@uniswap/v2-periphery/contracts/libraries/UniswapV2Library.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@uniswap/lib/contracts/libraries/TransferHelper.sol";

/**
 * FunFunv2
 * @author cool guy
 * @notice version 2.0.0
 * @notice compile with 0.6.6
 * @notice compile with 200 optimization for less gas.
*/
contract FunFunv2 {
    using SafeMath for uint256;

    /* The contract variables. */
    uint8 constant public VERSION = 2;

    /* The owner variable.  */
    address public Owner;

    /* The DEX variables. */
    IUniswapV2Router02 public Router;
    IUniswapV2Factory public Factory;
    IWETH public WETH;

    /* The owner only modifier. */
    modifier ownerOnly() {
        require(msg.sender == Owner, "FORBIDDEN");
        _;
    }

    /**
     * @dev The constructor.
     *
     * @param routerAddr: The router address.
     */
    constructor(address routerAddr) public {
        Owner = msg.sender;

        Router = IUniswapV2Router02(routerAddr);
        Factory = IUniswapV2Factory(Router.factory());
        WETH = IWETH(Router.WETH());
    }

    /**
     * @dev Executes a `swapExactETHForTokensSupportingFeeOnTransferTokens` and split's it to wallets.
     *
     * @notice msg.value must greater then (sellGasPricePerWallet + buyAmountPerWallet) * len(wallets)
     *
     * @param tokenAddr: The token address.
     * @param wallets: The wallet addresses to split the gain.
     * @param buyAmountPerWallet: Buy amount per wallet.
     * @param sellGasPricePerWallet: Estimated sell + approve gas price to send to the wallets.
     */
    function swapExactETHForTokens(
        address tokenAddr,
        address[] memory wallets,
        uint256 buyAmountPerWallet,
        uint256 sellGasPricePerWallet
    ) payable public ownerOnly {
        // Require msg.value.
        require(msg.value > 0, "NV"); // No Value

        // Snipe with multiple wallets.
        for (uint256 i = 0; i < wallets.length; i++) {
            // Swap.
            IWETH(WETH).deposit{value: buyAmountPerWallet}();
            assert(IWETH(WETH).transfer(Factory.getPair(address(WETH), tokenAddr), buyAmountPerWallet));

            uint balanceBefore = IERC20(tokenAddr).balanceOf(wallets[i]);
            _swap(address(WETH), tokenAddr, wallets[i]);
            require(
                IERC20(tokenAddr).balanceOf(wallets[i]).sub(balanceBefore) >= 1,
                'UniswapV2Router: INSUFFICIENT_OUTPUT_AMOUNT'
            );

            // Send ETH for selling.
            (bool success,) = wallets[i].call{ value: sellGasPricePerWallet }("");
            require(success, "GPSF"); // Gas price sending is failed.
        }

        // Refund remaining ETH.
        (bool success2,) = msg.sender.call{ value: address(this).balance }("");
        require(success2, "RF"); // Refund failed.
    }

    /**
     * @dev Executes `swapExactTokensForTokensSupportingFeeOnTransferTokens` internally and sends the ETH to owner.@author
     *
     * @notice The wallets needs to approve this contract.
     *
     * @param tokenAddr: The token address.
     * @param wallets: The wallet addresses to sell token amounts.
     */
    function swapExactTokensForETH(
        address tokenAddr,
        address[] memory wallets
    ) public ownerOnly {
        // Get the token.
        IERC20 token = IERC20(tokenAddr);

        // Sell with multiple wallets.
        for (uint256 i = 0; i < wallets.length; i++) {
            // Check token balance.
            require(token.balanceOf(wallets[i]) > 0, "NTA"); // No Token Amount

            // Check allowances.
            require(token.allowance(wallets[i], address(this)) >= token.balanceOf(wallets[i]), "NSA"); // No Sniper Allowance

            // The swapExactTokensForTokensSupportingFeeOnTransferTokens function from UniswapV2Router02.sol.
            TransferHelper.safeTransferFrom(
                tokenAddr, wallets[i], Factory.getPair(tokenAddr, address(WETH)), token.balanceOf(wallets[i])
            );
            _swap(tokenAddr, address(WETH), address(this));
            uint amountOut = IERC20(address(WETH)).balanceOf(address(this));
            require(amountOut >= 1, 'UniswapV2Router: INSUFFICIENT_OUTPUT_AMOUNT');
            IWETH(WETH).withdraw(amountOut);
            TransferHelper.safeTransferETH(address(Owner), amountOut);
        }

    }

    /**
     * @dev Internal swap function.
     *
     * @param fromAddr From token.
     * @param toAddr To Token.
     * @param to Swap receiver.
     */
    function _swap(address fromAddr, address toAddr, address to) internal virtual {
        // Sort tokens.
        (address token0,) = UniswapV2Library.sortTokens(fromAddr, toAddr);

        // Get pair.
        address pairAddr = Factory.getPair(fromAddr, toAddr);
        require(pairAddr != address(0), "NPA"); // No Pair Address
        IUniswapV2Pair pair = IUniswapV2Pair(pairAddr);

        // Get reserves.
        (uint reserve0, uint reserve1,) = pair.getReserves();
        (uint reserveInput, uint reserveOutput) = fromAddr == token0 ? (reserve0, reserve1) : (reserve1, reserve0);

        // Calculate amounts.
        uint amountInput = IERC20(fromAddr).balanceOf(address(pair)).sub(reserveInput);
        uint amountOutput = UniswapV2Library.getAmountOut(amountInput, reserveInput, reserveOutput);

        // Swap.
        (uint amount0Out, uint amount1Out) = fromAddr == token0 ? (uint(0), amountOutput) : (amountOutput, uint(0));
        pair.swap(amount0Out, amount1Out, to, new bytes(0));
    }

    // The WETH receiver.
    receive() external payable {
        assert(msg.sender == address(WETH)); // only accept ETH via fallback from the WETH contract
    }
}
