contract S303{

    address manager;
    using SafeMath for uint256;


    address payable m1;
    address payable  m2;
    address payable  m3;    
    uint public assocFee;    
    uint public remaining;
    uint public totalReceived;



    constructor (address payable _m1, address payable _m2, address payable _m3, uint _assocFee) public {
        m1 = _m1;
        m2 = _m2;
        m3 = _m3;
        assocFee = _assocFee;        
        remaining = 0;
        totalReceived = 0;
    }

    receive() external payable{
        require(msg.value >= assocFee, "Please send correct assocFee");

        uint aux = msg.value.add(remaining);
        uint dividends = aux.div(3);
        remaining = msg.value.add(remaining).mod(3);
        m1.transfer(dividends);
        m2.transfer(dividends);
        m3.transfer(dividends);       

        totalReceived = totalReceived.add(msg.value);

    }
}
