        _setRoyaltyWallet(owner());
        _setRoyaltyFee(maxRoyaltyFee);

        saleConfig.maxUserMint=2; // Default to 2 per user max
        saleConfig.unlockedSupplyMaxCount = 12; // DEfault to just 12 available to public in first release - max is 32

  }


        // Authorize someone other than the owner to change the sales status
      function authorizeSalesAdmin(address admin) external  onlyOwner{
            authAdmin=admin;

      }


    // Used to calculate the royalties
    function royaltyInfo(
    uint256, // tokenId, is not used for this 
    uint256 salePrice)
        external
        view override returns (
        address receiver,
        uint256 royaltyAmount
    )
        {
            return (treasury, salePrice * royaltyFee / ROYALTY_FEE_DENOMINATOR);
        }



    // Set up a white list signer for the presale by code (signed transactions)
     function setUpPresaleByCode(
        uint32 _userMaxCount, uint32 _releasedMaxCount, address wls
    ) external onlyOwner {

        // Check params
        require(wls != address(0), "Level1: zero address");

        codeSaleConfig.whitelistSigner = wls;
        codeSaleConfig.maxUserMint=_userMaxCount;
        codeSaleConfig.unlockedSupplyMaxCount=_releasedMaxCount;

        emit SalesCodeConfigChanged(wls);
    }


    function setUpPresale(uint32 _userMaxCount, uint32 _releasedMaxCount) external onlyOwner {
