    function setUpSale(uint32 _userMaxCount, uint32 _releasedMaxCount) external onlyOwner {
        saleConfig.maxUserMint=_userMaxCount;
        saleConfig.unlockedSupplyMaxCount = _releasedMaxCount;
    }


    // add to the list of people allowed to purchase in the private/presale

  function addToPresaleList(address[] calldata addresses) external override onlyOwner {
    for (uint256 i = 0; i < addresses.length; i++) {
      require(addresses[i] != address(0), "Can't add the null address");

            // enable the address
      _allowList[addresses[i]] = true;

        // keep track of how many they have claimed (do not reset by mistake)
      _allowListClaimed[addresses[i]] > 0 ? _allowListClaimed[addresses[i]] : 0;
    }
  }


    // check if a user is on the allow list, and if so, how many have they claimed
  function onPresaleList(address addr) external view override returns (bool) {
    return _allowList[addr];
  }

    // mark addresses as not allowed
  function removeFromPresaleList(address[] calldata addresses) external override onlyOwner {
    for (uint256 i = 0; i < addresses.length; i++) {
      require(addresses[i] != address(0), "Can't add the null address");

      /// @dev We don't want to reset possible _allowListClaimed numbers.
      _allowList[addresses[i]] = false;
    }
  }


