    require(totalPublicSupply+numberOfTokens <= CA_PUBLIC, 'Purchase would exceed total supply.');
    require(totalPublicSupply+numberOfTokens <= saleConfig.unlockedSupplyMaxCount, "Purchase would exceed supply released.");

    for (uint256 i = 0; i < numberOfTokens; i++) {
      /**
      * @dev Since they can get here while exceeding the CA_MAX,
      * we have to make sure to not mint any additional tokens.
      */
      if (totalPublicSupply < CA_PUBLIC) {
        /**
        * @dev Public token numbering starts after CA_GIFT.
        * And we don't want our tokens to start at 0 but at 1.
        */
        uint256 tokenId = CA_GIFT + totalPublicSupply + 1;

        totalPublicSupply += 1;
        _safeMint(target, tokenId);
      }
    }
  }

    // Do a regular purchase/mint of an item
  function mint(uint256 numberOfTokens) nonReentrant external override payable {
    require(isActive, 'Contract is not active');
    require(!isAllowListActive, 'Only allowing from presale list');
    require(totalSupply() < CA_MAX, 'All tokens have been minted');
    require(numberOfTokens <= saleConfig.maxUserMint, 'Purchase would exceed user minting limit.');
    require(totalPublicSupply+numberOfTokens <= CA_PUBLIC, 'Purchase would exceed total supply.');
    require(totalPublicSupply+numberOfTokens <= saleConfig.unlockedSupplyMaxCount, "Purchase would exceed supply released.");
    require(tokenPrice * numberOfTokens <= msg.value, 'ETH amount is not sufficient');


    for (uint256 i = 0; i < numberOfTokens; i++) {
      /**
      * @dev Since they can get here while exceeding the CA_MAX,
      * we have to make sure to not mint any additional tokens.
      */
      if (totalPublicSupply < CA_PUBLIC) {
        /**
        * @dev Public token numbering starts after CA_GIFT.
        * And we don't want our tokens to start at 0 but at 1.
        */
        uint256 tokenId = CA_GIFT + totalPublicSupply + 1;

        totalPublicSupply += 1;
        _safeMint(msg.sender, tokenId);
      }
    }
  }

  function mintPresale(uint256 numberOfTokens) nonReentrant external override payable {
    require(isActive, 'Contract is not active');
    require(isAllowListActive, 'Allow List is not active');
    require(_allowList[msg.sender], 'You are not on the Allow List');
    require(totalSupply() < CA_MAX, 'All tokens have been minted');
    require(numberOfTokens <= allowListMaxMint, 'Purchase over minting limit.');
    require(totalPublicSupply + numberOfTokens <= CA_PUBLIC, 'Purchase would exceed total supply.');
    require(totalPublicSupply + numberOfTokens <= presaleConfig.unlockedPresaleSupplyMaxCount, 'Purchase would exceed maximum presale supply.');
    require(_allowListClaimed[msg.sender] + numberOfTokens <= allowListMaxMint, 'Purchase exceeds max allowed');
    require(tokenPrice * numberOfTokens <= msg.value, 'ETH amount is not sufficient');

    for (uint256 i = 0; i < numberOfTokens; i++) {
      /**
      * @dev Public token numbering starts after CA_GIFT.
      * We don't want our tokens to start at 0 but at 1.
      */
      uint256 tokenId = CA_GIFT + totalPublicSupply + 1;

      totalPublicSupply += 1;
      _allowListClaimed[msg.sender] += 1;
      _safeMint(msg.sender, tokenId);
    }
  }

  function gift(address[] calldata to) nonReentrant external override onlyOwner {
    require(totalSupply() < CA_MAX, 'All tokens have been minted');
    require(totalGiftSupply + to.length <= CA_GIFT, 'Not enough tokens left to gift');
  emit tokenSentTo(to[0]);
    for(uint256 i = 0; i < to.length; i++) {
      /// @dev We don't want our tokens to start at 0 but at 1.
      uint256 tokenId = totalGiftSupply + 1;

      totalGiftSupply += 1;
      _safeMint(to[i], tokenId);
      emit tokenSentTo(to[i]);
    }
  }

   function mintCodeSaleTokens(
        uint256 count,
        uint256 maxCount,
        bytes calldata signature
    ) nonReentrant external payable {
        // Gas optimization
        uint256 _nextTokenId = nextTokenId;

        // Make sure presale has been set up
        require(codeSaleConfig.whitelistSigner != address(0), "Level1: presale not configured");
        require(codeSaleConfig.whitelistSigner != address(0), "Level1: presale not configured");

        require(treasury != address(0), "Level1: treasury not set");
        require(tokenPrice > 0, "Level1: token price not set");
        require(count > 0, "Level1: invalid count");
        require(isSalesCodeActive, "Sales by code not active.");

        require(_nextTokenId + count <= CA_MAX, "Level1: max supply exceeded");
        require(tokenPrice * count == msg.value, "Level1: incorrect Ether value");


        // Verify EIP-712 signature
        bytes32 digest = keccak256(
            abi.encodePacked("\x19\x01", DOMAIN_SEPARATOR, keccak256(abi.encode(PRESALE_TYPEHASH, msg.sender, maxCount)))
        );
        address recoveredAddress = ECDSA.recover(digest,signature); 
        require(
            recoveredAddress != address(0) && recoveredAddress == codeSaleConfig.whitelistSigner,
            "Level1: invalid signature"
        );

        require(salesCodeBoughtCounts[msg.sender] + count <= maxCount, "Level1: presale max count exceeded");
        salesCodeBoughtCounts[msg.sender] += count;

        // The contract never holds any Ether. Everything gets redirected to treasury directly.
        treasury.transfer(msg.value);

        for (uint256 ind = 0; ind < count; ind++) {
            _safeMint(msg.sender, _nextTokenId + ind);
        }
        nextTokenId += count;

        emit PresaleMint(msg.sender, count);
    }



